import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import LocationSelector from '../components/LocationSelector';

const API_URL = 'http://localhost:5000/api';

function CreateRequest({ user }) {
  const [formData, setFormData] = useState({
    item: '',
    store: '',
    price: '',
    deliveryLocation: user?.homeAddress || null,
    notes: ''
  });
  const [useHomeAddress, setUseHomeAddress] = useState(true);
  const [showDeliveryMap, setShowDeliveryMap] = useState(false);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleLocationChange = (type, field, value) => {
    setFormData(prev => ({
      ...prev,
      [type]: {
        ...prev[type],
        [field]: value
      }
    }));
  };

  const handleDeliveryLocationSelect = (type, location) => {
    setFormData(prev => ({
      ...prev,
      deliveryLocation: location
    }));
    setShowDeliveryMap(false);
  };

  const handleDeliveryAddressChange = () => {
    if (!user?.homeAddress) {
      navigate('/profile');
      return;
    }
    setUseHomeAddress(!useHomeAddress);
    setFormData(prev => ({
      ...prev,
      deliveryLocation: useHomeAddress ? null : user.homeAddress
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.item || !formData.store || !formData.price) {
      alert('Please fill in all required fields');
      return;
    }
    if (!formData.deliveryLocation) {
      alert('Please set delivery location');
      return;
    }

    setLoading(true);
    try {
      const requestData = {
        ...formData,
        customerId: user._id,
        price: parseFloat(formData.price)
      };

      await axios.post(`${API_URL}/requests`, requestData);
      alert('Request created successfully!');
      navigate('/');
    } catch (error) {
      alert('Error creating request. Please try again.');
    }
    setLoading(false);
  };

  return (
    <div className="container">
      <div className="app-header">
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <button 
            onClick={() => navigate('/')}
            style={{ background: 'none', border: 'none', color: 'white', fontSize: '24px', marginRight: '15px' }}
          >
            ←
          </button>
          <div>
            <div className="car-icon">🚗</div>
            <h1 className="app-title">Delivery</h1>
          </div>
        </div>
      </div>

      {!user?.homeAddress && (
        <div className="card" style={{ background: '#fff3cd', border: '1px solid #ffeaa7' }}>
          <p>⚠️ Please set your home address first</p>
          <button 
            type="button"
            onClick={() => navigate('/profile')}
            className="btn-secondary"
          >
            Set Home Address
          </button>
        </div>
      )}

      <div style={{ display: 'flex', justifyContent: 'space-between', margin: '15px 0' }}>
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <div style={{ width: '12px', height: '12px', background: '#4CAF50', borderRadius: '50%', marginRight: '10px' }}></div>
          <span>Pickup Location</span>
        </div>
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <div style={{ width: '12px', height: '12px', background: '#333', borderRadius: '50%', marginRight: '10px' }}></div>
          <span>Delivery Location</span>
        </div>
      </div>

      <form onSubmit={handleSubmit}>
        <div className="card">
          <div className="input-group">
            <label>What do you need? *</label>
            <input
              type="text"
              name="item"
              placeholder="e.g., Bread, Medicine, Groceries"
              value={formData.item}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="input-group">
            <label>From which store? *</label>
            <input
              type="text"
              name="store"
              placeholder="e.g., Carrefour, Marjane, Local Pharmacy"
              value={formData.store}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="input-group">
            <label>Pickup Location *</label>
            <div style={{ display: 'flex', gap: '10px', alignItems: 'center' }}>
              <input 
                type="checkbox" 
                checked={useHomeAddress}
                onChange={handlePickupChange}
              />
              <span>Use my home address</span>
            </div>
            {formData.pickupLocation && (
              <div style={{ padding: '8px', background: '#f0f8f0', borderRadius: '4px', marginTop: '5px' }}>
                📍 {formData.pickupLocation.address}
              </div>
            )}
          </div>

          <div className="input-group">
            <label>Delivery Location *</label>
            <button
              type="button"
              onClick={() => setShowDeliveryMap(true)}
              style={{
                width: '100%',
                padding: '12px',
                border: '2px dashed #ccc',
                borderRadius: '8px',
                background: 'white',
                cursor: 'pointer'
              }}
            >
              {formData.deliveryLocation ? 
                `📍 ${formData.deliveryLocation.address}` : 
                '🗺️ Select delivery location on map'
              }
            </button>
          </div>

          <div className="input-group">
            <label>How much will you pay the driver? *</label>
            <input
              type="number"
              name="price"
              placeholder="Amount in MAD"
              value={formData.price}
              onChange={handleInputChange}
              min="5"
              required
            />
            <small style={{ color: '#666', fontSize: '12px' }}>
              This is what you'll pay the driver for the service (not including item cost)
            </small>
          </div>

          <div className="input-group">
            <label>Additional Notes</label>
            <textarea
              name="notes"
              placeholder="Any special instructions..."
              value={formData.notes}
              onChange={handleInputChange}
              rows="3"
            />
          </div>
        </div>

        <button 
          type="submit" 
          className="btn-secondary"
          disabled={loading || !formData.pickupLocation || !formData.deliveryLocation}
          style={{ margin: '20px 0' }}
        >
          {loading ? 'Creating Request...' : 'Set fare & request'}
        </button>
      </form>

      {showDeliveryMap && (
        <div style={{
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: 'white',
          zIndex: 1000,
          padding: '20px'
        }}>
          <div style={{ display: 'flex', alignItems: 'center', marginBottom: '20px' }}>
            <button 
              onClick={() => setShowDeliveryMap(false)}
              style={{ background: 'none', border: 'none', fontSize: '24px', marginRight: '15px' }}
            >
              ←
            </button>
            <h2>Select Delivery Location</h2>
          </div>

          <LocationSelector
            onLocationSelect={handleDeliveryLocationSelect}
            deliveryLocation={formData.deliveryLocation}
            mode="delivery"
          />
        </div>
      )}
    </div>
  );
}

export default CreateRequest;
