import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';

const DriverLocationSharer = ({ user, activeDelivery }) => {
  const [socket, setSocket] = useState(null);
  const [isSharing, setIsSharing] = useState(false);
  const [currentLocation, setCurrentLocation] = useState(null);

  useEffect(() => {
    if (user?.userType === 'driver' && activeDelivery) {
      const newSocket = io('http://192.168.11.106:5000');
      setSocket(newSocket);
      startLocationSharing(newSocket);

      return () => {
        newSocket.disconnect();
        stopLocationSharing();
      };
    }
  }, [user, activeDelivery]);

  const startLocationSharing = (socketConnection) => {
    if (navigator.geolocation) {
      setIsSharing(true);

      const watchId = navigator.geolocation.watchPosition(
        (position) => {
          const location = {
            latitude: position.coords.latitude,
            longitude: position.coords.longitude,
            address: `Moving towards you...`,
            accuracy: position.coords.accuracy
          };

          setCurrentLocation(location);

          if (socketConnection && activeDelivery) {
            console.log('üìç Sending driver location:', location);
            socketConnection.emit('driver-location', {
              driverId: user._id,
              requestId: activeDelivery._id,
              location: location,
              timestamp: new Date().toISOString()
            });
          }
        },
        (error) => {
          console.error('Location error:', error);
          setIsSharing(false);
        },
        {
          enableHighAccuracy: true,
          timeout: 10000,
          maximumAge: 3000
        }
      );

      return () => {
        navigator.geolocation.clearWatch(watchId);
        setIsSharing(false);
      };
    }
  };

  const stopLocationSharing = () => {
    setIsSharing(false);
  };

  if (user?.userType !== 'driver' || !activeDelivery) {
    return null;
  }

  return (
    <div style={{
      background: isSharing ? '#4CAF50' : '#FF9800',
      color: 'white',
      padding: '10px',
      borderRadius: '8px',
      margin: '10px 0',
      textAlign: 'center'
    }}>
      <div style={{ fontSize: '16px', fontWeight: 'bold', marginBottom: '5px' }}>
        üìç Location Sharing: {isSharing ? 'ON' : 'OFF'}
      </div>
      {currentLocation && (
        <div style={{ fontSize: '12px', opacity: 0.9 }}>
          {currentLocation.address}
        </div>
      )}
      <div style={{ fontSize: '11px', opacity: 0.8, marginTop: '5px' }}>
        Customer can see your live location
      </div>
    </div>
  );
};

export default DriverLocationSharer;
