import React, { useEffect, useRef, useState } from 'react';

const LocationSelector = ({ onLocationSelect, pickupLocation, deliveryLocation }) => {
  const mapRef = useRef(null);
  const [map, setMap] = useState(null);
  const [markers, setMarkers] = useState({ pickup: null, delivery: null });
  const [mode, setMode] = useState('pickup'); // 'pickup' or 'delivery'

  useEffect(() => {
    if (!window.google) {
      const script = document.createElement('script');
      script.src = `https://maps.googleapis.com/maps/api/js?key=AIzaSyBFw0Qbyq9zTFTd-tUY6dO_X0VLhTlHCdc&libraries=geometry`;
      script.async = true;
      script.defer = true;
      script.onload = initMap;
      document.head.appendChild(script);
    } else {
      initMap();
    }
  }, []);

  useEffect(() => {
    if (map) {
      updateMarkers();
    }
  }, [pickupLocation, deliveryLocation, map]);

  const initMap = () => {
    const mapInstance = new window.google.maps.Map(mapRef.current, {
      center: { lat: 33.5731, lng: -7.5898 },
      zoom: 13
    });

    mapInstance.addListener('click', (event) => {
      const lat = event.latLng.lat();
      const lng = event.latLng.lng();

      onLocationSelect(mode, {
        latitude: lat,
        longitude: lng,
        address: `${lat.toFixed(4)}, ${lng.toFixed(4)}`
      });
    });

    setMap(mapInstance);
  };

  const updateMarkers = () => {
    if (!map) return;

    // Clear existing markers
    if (markers.pickup) markers.pickup.setMap(null);
    if (markers.delivery) markers.delivery.setMap(null);

    const newMarkers = { pickup: null, delivery: null };

    // Add pickup marker
    if (pickupLocation?.latitude && pickupLocation?.longitude) {
      newMarkers.pickup = new window.google.maps.Marker({
        position: { lat: pickupLocation.latitude, lng: pickupLocation.longitude },
        map: map,
        title: 'Pickup Location',
        icon: {
          path: window.google.maps.SymbolPath.CIRCLE,
          scale: 10,
          fillColor: '#4CAF50',
          fillOpacity: 1,
          strokeColor: 'white',
          strokeWeight: 2
        }
      });
    }

    // Add delivery marker
    if (deliveryLocation?.latitude && deliveryLocation?.longitude) {
      newMarkers.delivery = new window.google.maps.Marker({
        position: { lat: deliveryLocation.latitude, lng: deliveryLocation.longitude },
        map: map,
        title: 'Delivery Location',
        icon: {
          path: window.google.maps.SymbolPath.CIRCLE,
          scale: 10,
          fillColor: '#333',
          fillOpacity: 1,
          strokeColor: 'white',
          strokeWeight: 2
        }
      });
    }

    setMarkers(newMarkers);
  };

  return (
    <div>
      <div style={{ display: 'flex', gap: '10px', marginBottom: '10px' }}>
        <button
          type="button"
          onClick={() => setMode('pickup')}
          style={{
            padding: '8px 16px',
            border: 'none',
            borderRadius: '20px',
            background: mode === 'pickup' ? '#4CAF50' : '#e0e0e0',
            color: mode === 'pickup' ? 'white' : '#333',
            cursor: 'pointer'
          }}
        >
          🟢 Set Pickup
        </button>
        <button
          type="button"
          onClick={() => setMode('delivery')}
          style={{
            padding: '8px 16px',
            border: 'none',
            borderRadius: '20px',
            background: mode === 'delivery' ? '#333' : '#e0e0e0',
            color: mode === 'delivery' ? 'white' : '#333',
            cursor: 'pointer'
          }}
        >
          ⚫ Set Delivery
        </button>
      </div>

      <div 
        ref={mapRef} 
        style={{ 
          width: '100%', 
          height: '250px', 
          borderRadius: '15px',
          border: '2px solid #e0e0e0'
        }} 
      />

      <p style={{ fontSize: '12px', color: '#666', margin: '5px 0' }}>
        Click on the map to set {mode} location
      </p>
    </div>
  );
};

export default LocationSelector;
