import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';


const API_URL = 'http://192.168.11.106:5000/api';

function CustomerDashboard({ user }) {
  const [requests, setRequests] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    loadMyRequests();
  }, []);

  const loadMyRequests = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`${API_URL}/requests/my/${user._id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setRequests(response.data);
    } catch (error) {
      console.error('Error loading requests:', error);
    }
    setLoading(false);
  };

  const getStatusColor = (status) => {
    const colors = {
      pending: '#FF9800',
      accepted: '#4CAF50',
      shopping: '#2196F3',
      delivering: '#9C27B0',
      completed: '#4CAF50',
      cancelled: '#F44336'
    };
    return colors[status] || '#666';
  };

  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    window.location.href = '/login';
  };

  return (
    <div className="container">
      <div className="app-header">
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <div>
            <div className="car-icon">üöÄ</div>
            <h1 className="app-title">Delivery</h1>
          </div>
          <div style={{ textAlign: 'right' }}>
            <div style={{ fontSize: '14px', opacity: 0.9 }}>Welcome</div>
            <div style={{ fontSize: '18px', fontWeight: 'bold' }}>{user.name}</div>
          </div>
        </div>
      </div>

      <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '10px', margin: '20px 0' }}>
        <button 
          className="btn-primary"
          onClick={() => navigate('/create-request')}
        >
          + New Request
        </button>
        <button 
          className="btn-secondary"
          onClick={() => navigate('/drivers-map')}
        >
          üó∫Ô∏è View Drivers
        </button>
      </div>

      <div style={{ display: 'flex', justifyContent: 'space-between', margin: '20px 0' }}>
        <h3>My Requests</h3>
        <button onClick={logout} style={{ background: 'none', border: 'none', color: '#666' }}>
          Logout
        </button>
      </div>

      {loading ? (
        <div className="loading">Loading requests...</div>
      ) : requests.length === 0 ? (
        <div className="card" style={{ textAlign: 'center', padding: '40px' }}>
          <p style={{ color: '#666', fontSize: '16px' }}>No requests yet</p>
          <p style={{ color: '#999', fontSize: '14px' }}>Create your first delivery request!</p>
        </div>
      ) : (
        requests.map(request => (
          <div key={request._id} className="request-card">
            <div className="request-header">
              <div>
                <h4 style={{ margin: 0, color: '#333' }}>{request.item}</h4>
                <p style={{ margin: '5px 0', color: '#666', fontSize: '14px' }}>
                  from {request.store}
                </p>
              </div>
              <div className="request-price">{request.price} MAD</div>
            </div>

            <div className="request-details">
              <p><strong>Status:</strong> 
                <span style={{ color: getStatusColor(request.status), marginLeft: '5px' }}>
                  {request.status.charAt(0).toUpperCase() + request.status.slice(1)}
                </span>
              </p>
              <p><strong>Pickup:</strong> {request.pickupLocation.address}</p>
              <p><strong>Delivery:</strong> {request.deliveryLocation.address}</p>
              {request.driverId && (
                <p><strong>Driver:</strong> {request.driverId.name}</p>
              )}
            </div>

            {request.status !== 'completed' && request.status !== 'cancelled' && (
              <button 
                className="btn-secondary"
                onClick={() => navigate(`/track/${request._id}`)}
                style={{ marginTop: '15px' }}
              >
                Track Delivery
              </button>
            )}
          </div>
        ))
      )}


    </div>
  );
}

export default CustomerDashboard;
