import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import LocationSelector from '../components/LocationSelector';

const API_URL = 'http://localhost:5000/api';

function CreateRequest({ user }) {
  const [formData, setFormData] = useState({
    item: '',
    store: '',
    price: '',
    pickupLocation: {
      address: '',
      latitude: 33.5731,
      longitude: -7.5898 // Default to Casablanca
    },
    deliveryLocation: {
      address: '',
      latitude: 33.5731,
      longitude: -7.5898
    },
    notes: ''
  });
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleLocationChange = (type, field, value) => {
    setFormData(prev => ({
      ...prev,
      [type]: {
        ...prev[type],
        [field]: value
      }
    }));
  };

  const handleMapLocationSelect = (type, location) => {
    setFormData(prev => ({
      ...prev,
      [`${type}Location`]: location
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.item || !formData.store || !formData.price) {
      alert('Please fill in all required fields');
      return;
    }

    setLoading(true);
    try {
      const requestData = {
        ...formData,
        customerId: user._id,
        price: parseFloat(formData.price)
      };

      await axios.post(`${API_URL}/requests`, requestData);
      alert('Request created successfully!');
      navigate('/');
    } catch (error) {
      alert('Error creating request. Please try again.');
    }
    setLoading(false);
  };

  return (
    <div className="container">
      <div className="app-header">
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <button 
            onClick={() => navigate('/')}
            style={{ background: 'none', border: 'none', color: 'white', fontSize: '24px', marginRight: '15px' }}
          >
            ←
          </button>
          <div>
            <div className="car-icon">🚗</div>
            <h1 className="app-title">Delivery</h1>
          </div>
        </div>
      </div>

      <div style={{ margin: '15px 0' }}>
        <LocationSelector
          onLocationSelect={handleMapLocationSelect}
          pickupLocation={formData.pickupLocation}
          deliveryLocation={formData.deliveryLocation}
        />
      </div>

      <div style={{ display: 'flex', justifyContent: 'space-between', margin: '15px 0' }}>
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <div style={{ width: '12px', height: '12px', background: '#4CAF50', borderRadius: '50%', marginRight: '10px' }}></div>
          <span>Pickup Location</span>
        </div>
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <div style={{ width: '12px', height: '12px', background: '#333', borderRadius: '50%', marginRight: '10px' }}></div>
          <span>Delivery Location</span>
        </div>
      </div>

      <form onSubmit={handleSubmit}>
        <div className="card">
          <div className="input-group">
            <label>What do you need? *</label>
            <input
              type="text"
              name="item"
              placeholder="e.g., Bread, Medicine, Groceries"
              value={formData.item}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="input-group">
            <label>From which store? *</label>
            <input
              type="text"
              name="store"
              placeholder="e.g., Carrefour, Marjane, Local Pharmacy"
              value={formData.store}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="input-group">
            <label>Pickup Address *</label>
            <input
              type="text"
              placeholder="Enter pickup address"
              value={formData.pickupLocation.address}
              onChange={(e) => handleLocationChange('pickupLocation', 'address', e.target.value)}
              required
            />
          </div>

          <div className="input-group">
            <label>Delivery Address *</label>
            <input
              type="text"
              placeholder="Enter delivery address"
              value={formData.deliveryLocation.address}
              onChange={(e) => handleLocationChange('deliveryLocation', 'address', e.target.value)}
              required
            />
          </div>

          <div className="input-group">
            <label>How much will you pay the driver? *</label>
            <input
              type="number"
              name="price"
              placeholder="Amount in MAD"
              value={formData.price}
              onChange={handleInputChange}
              min="5"
              required
            />
            <small style={{ color: '#666', fontSize: '12px' }}>
              This is what you'll pay the driver for the service (not including item cost)
            </small>
          </div>

          <div className="input-group">
            <label>Additional Notes</label>
            <textarea
              name="notes"
              placeholder="Any special instructions..."
              value={formData.notes}
              onChange={handleInputChange}
              rows="3"
            />
          </div>
        </div>

        <button 
          type="submit" 
          className="btn-secondary"
          disabled={loading}
          style={{ margin: '20px 0' }}
        >
          {loading ? 'Creating Request...' : 'Set fare & request'}
        </button>
      </form>
    </div>
  );
}

export default CreateRequest;
