import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import io from 'socket.io-client';

const API_URL = 'http://192.168.1.227:5000/api';

function CustomerDashboard({ user }) {
  const [requests, setRequests] = useState([]);
  const [availableDrivers, setAvailableDrivers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('home');
  const mapRef = useRef(null);
  const [map, setMap] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    loadMyRequests();
    loadAvailableDrivers();

    const socket = io('http://192.168.1.227:5000');
    socket.on('driver-locations', (drivers) => {
      console.log('Received drivers:', drivers);
      setAvailableDrivers(drivers);
    });

    socket.emit('get-driver-locations');

    return () => socket.disconnect();
  }, []);

  useEffect(() => {
    initMap();
    updateDriversOnMap(availableDrivers);
  }, [availableDrivers]);

  const loadMyRequests = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`${API_URL}/requests/my/${user._id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setRequests(response.data);
    } catch (error) {
      console.error('Error loading requests:', error);
    }
    setLoading(false);
  };

  const loadAvailableDrivers = async () => {
    try {
      const response = await axios.get(`${API_URL}/drivers/available`);
      setAvailableDrivers(response.data);
    } catch (error) {
      console.error('Error loading drivers:', error);
    }
  };

  const initMap = () => {
    if (!mapRef.current) return;

    if (window.google && window.google.maps) {
      const mapInstance = new window.google.maps.Map(mapRef.current, {
        zoom: 14,
        center: { lat: 33.5731, lng: -7.5898 },
        styles: [
          { featureType: "poi", stylers: [{ visibility: "off" }] },
          { featureType: "transit", stylers: [{ visibility: "off" }] }
        ],
        disableDefaultUI: true,
        zoomControl: true
      });
      setMap(mapInstance);
    } else {
      // Load Google Maps API
      const script = document.createElement('script');
      script.src = 'https://maps.googleapis.com/maps/api/js?key=YOUR_API_KEY&callback=initMap';
      script.async = true;
      document.head.appendChild(script);

      window.initMap = () => {
        const mapInstance = new window.google.maps.Map(mapRef.current, {
          zoom: 14,
          center: { lat: 33.5731, lng: -7.5898 },
          disableDefaultUI: true,
          zoomControl: true
        });
        setMap(mapInstance);
      };
    }
  };

  const updateDriversOnMap = (drivers) => {
    if (!mapRef.current) return;

    const markersContainer = mapRef.current.querySelector('#driver-markers');
    if (!markersContainer) {
      // Re-initialize map if markers container doesn't exist
      initMap();
      setTimeout(() => updateDriversOnMap(drivers), 100);
      return;
    }

    // Clear existing markers
    markersContainer.innerHTML = '';

    console.log('Updating map with drivers:', drivers.length);

    // Add driver markers
    drivers.forEach((driver, index) => {
      console.log('Adding driver to map:', driver.name);
      const marker = document.createElement('div');
      marker.style.cssText = `
        position: absolute;
        top: 50%;
        left: 50%;
        transform: translate(-50%, -50%);
        width: 50px;
        height: 50px;
        background: linear-gradient(135deg, #28a745 0%, #20c997 100%);
        border-radius: 50%;
        display: flex;
        align-items: center;
        justify-content: center;
        color: white;
        font-weight: bold;
        font-size: 20px;
        box-shadow: 0 4px 15px rgba(40, 167, 69, 0.8);
        cursor: pointer;
        animation: pulse 2s infinite;
        border: 3px solid white;
        z-index: 1000;
        pointer-events: auto;
      `;

      marker.innerHTML = driver.name?.charAt(0) || 'D';
      marker.title = `${driver.name} - ⭐${driver.rating?.toFixed(1) || '5.0'} - Online`;

      // Add driver name label
      const label = document.createElement('div');
      label.style.cssText = `
        position: absolute;
        top: 70px;
        left: 50%;
        transform: translateX(-50%);
        background: rgba(0,0,0,0.8);
        color: white;
        padding: 4px 8px;
        border-radius: 12px;
        font-size: 12px;
        white-space: nowrap;
      `;
      label.innerHTML = driver.name || 'Driver';
      marker.appendChild(label);

      markersContainer.appendChild(marker);
    });

    // Add pulse animation
    if (!document.getElementById('pulse-style')) {
      const style = document.createElement('style');
      style.id = 'pulse-style';
      style.textContent = `
        @keyframes pulse {
          0% { transform: scale(1); box-shadow: 0 4px 12px rgba(40, 167, 69, 0.4); }
          50% { transform: scale(1.1); box-shadow: 0 6px 20px rgba(40, 167, 69, 0.6); }
          100% { transform: scale(1); box-shadow: 0 4px 12px rgba(40, 167, 69, 0.4); }
        }
      `;
      document.head.appendChild(style);
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    window.location.href = '/login';
  };

  if (activeTab === 'drivers') {
    return (
      <div className="container">
        <div className="indrive-header">
          <div className="header-top">
            <button className="menu-btn" onClick={() => setActiveTab('home')}>←</button>
            <h2>Available Drivers</h2>
            <div></div>
          </div>
        </div>

        <div className="driver-list">
          {availableDrivers.length === 0 ? (
            <div className="empty-state">
              <div className="empty-icon">🚗</div>
              <div>No drivers available</div>
              <div style={{ fontSize: '14px', marginTop: '8px' }}>Try again in a few minutes</div>
            </div>
          ) : (
            availableDrivers.map(driver => (
              <div key={driver._id} className="driver-item">
                <div className="driver-avatar">
                  {driver.name?.charAt(0) || 'D'}
                </div>
                <div className="driver-info">
                  <div className="driver-name">{driver.name || 'Unknown Driver'}</div>
                  <div className="driver-rating">⭐ {driver.rating?.toFixed(1) || '5.0'} ({driver.totalReviews || 0} reviews)</div>
                  <div className="driver-vehicle">{driver.vehicle?.type || 'Car'} {driver.vehicle?.model || 'Vehicle'}</div>
                </div>
                <div className="driver-status">
                  <div className="online-indicator">🟢 Online</div>
                  <div className="driver-distance">2 min away</div>
                </div>
              </div>
            ))
          )}
        </div>

        <div className="bottom-nav">
          <button className="nav-item" onClick={() => setActiveTab('home')}>
            <div className="nav-icon">🏠</div>
            <div>Home</div>
          </button>
          <button className="nav-item active">
            <div className="nav-icon">👥</div>
            <div>Drivers</div>
          </button>
          <button className="nav-item" onClick={() => setActiveTab('orders')}>
            <div className="nav-icon">📦</div>
            <div>Orders</div>
          </button>
          <button className="nav-item" onClick={() => navigate('/profile')}>
            <div className="nav-icon">👤</div>
            <div>Profile</div>
          </button>
        </div>
      </div>
    );
  }

  if (activeTab === 'orders') {
    return (
      <div className="container">
        <div className="indrive-header">
          <div className="header-top">
            <button className="menu-btn" onClick={() => setActiveTab('home')}>←</button>
            <h2>My Orders</h2>
            <div></div>
          </div>
        </div>

        <div className="recent-orders">
          {requests.map(request => (
            <div key={request._id} className="order-item" onClick={() => navigate(`/track/${request._id}`)}>
              <div className="order-header">
                <div>
                  <div className="order-title">{request.item}</div>
                  <div className="order-store">from {request.store}</div>
                </div>
                <div className={`order-status status-${request.status}`}>
                  {request.status}
                </div>
              </div>
              <div className="order-price">{request.price} MAD</div>
            </div>
          ))}
        </div>

        <div className="bottom-nav">
          <button className="nav-item" onClick={() => setActiveTab('home')}>
            <div className="nav-icon">🏠</div>
            <div>Home</div>
          </button>
          <button className="nav-item" onClick={() => setActiveTab('drivers')}>
            <div className="nav-icon">👥</div>
            <div>Drivers</div>
          </button>
          <button className="nav-item active">
            <div className="nav-icon">📦</div>
            <div>Orders</div>
          </button>
          <button className="nav-item" onClick={() => navigate('/profile')}>
            <div className="nav-icon">👤</div>
            <div>Profile</div>
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      {/* inDrive Header */}
      <div className="indrive-header">
        <div className="header-top">
          <button className="menu-btn">☰</button>
          <button className="profile-btn" onClick={() => navigate('/profile')}>
            {user.name?.charAt(0) || 'U'}
          </button>
        </div>

        <div className="location-section">
          <div className="location-label">Your location</div>
          <div className="location-text">
            📍 {user.address || 'Set your location'}
          </div>
        </div>
      </div>

      {/* Map */}
      <div className="map-section">
        <div className="map-overlay">
          <div>📍 Find drivers nearby</div>
          <div className="drivers-online">{availableDrivers.length} online</div>
        </div>
        <div ref={mapRef} style={{ width: '100%', height: '100%' }} />
      </div>

      {/* Action Buttons */}
      <div className="action-buttons">
        <button className="main-action" onClick={() => navigate('/create-request')}>
          🚀 Request Delivery
        </button>

        <div className="secondary-actions">
          <button className="secondary-btn" onClick={() => setActiveTab('drivers')}>
            <span className="icon">👥</span>
            <div>Drivers</div>
            <div style={{ fontSize: '11px', color: '#666' }}>{availableDrivers.length} available</div>
          </button>
          <button className="secondary-btn" onClick={() => navigate('/drivers-map')}>
            <span className="icon">🗺️</span>
            <div>Map View</div>
            <div style={{ fontSize: '11px', color: '#666' }}>Live tracking</div>
          </button>
        </div>
      </div>

      {/* Recent Orders */}
      <div className="recent-orders">
        <div className="section-title">Recent orders</div>

        {loading ? (
          <div style={{ textAlign: 'center', padding: '20px' }}>Loading...</div>
        ) : requests.length === 0 ? (
          <div className="empty-state">
            <div className="empty-icon">📦</div>
            <div>No orders yet</div>
            <div style={{ fontSize: '14px', marginTop: '8px' }}>Your delivery history will appear here</div>
          </div>
        ) : (
          requests.slice(0, 3).map(request => (
            <div key={request._id} className="order-item" onClick={() => navigate(`/track/${request._id}`)}>
              <div className="order-header">
                <div>
                  <div className="order-title">{request.item}</div>
                  <div className="order-store">from {request.store}</div>
                </div>
                <div className={`order-status status-${request.status}`}>
                  {request.status}
                </div>
              </div>
              <div className="order-price">{request.price} MAD</div>
            </div>
          ))
        )}
      </div>

      {/* Bottom Navigation */}
      <div className="bottom-nav">
        <button className="nav-item active">
          <div className="nav-icon">🏠</div>
          <div>Home</div>
        </button>
        <button className="nav-item" onClick={() => setActiveTab('drivers')}>
          <div className="nav-icon">👥</div>
          <div>Drivers</div>
        </button>
        <button className="nav-item" onClick={() => setActiveTab('orders')}>
          <div className="nav-icon">📦</div>
          <div>Orders</div>
        </button>
        <button className="nav-item" onClick={() => navigate('/profile')}>
          <div className="nav-icon">👤</div>
          <div>Profile</div>
        </button>
      </div>
    </div>
  );
}

export default CustomerDashboard;
