import React, { useEffect, useState, useRef } from 'react';
import io from 'socket.io-client';

const LiveTrackingMap = ({ request, user }) => {
  const [driverLocation, setDriverLocation] = useState(null);
  const [lastUpdate, setLastUpdate] = useState(null);
  const [socket, setSocket] = useState(null);
  const mapRef = useRef(null);
  const [map, setMap] = useState(null);
  const [driverMarker, setDriverMarker] = useState(null);

  useEffect(() => {
    const newSocket = io('http://localhost:5000');
    setSocket(newSocket);
    newSocket.emit('join-room', request._id);

    newSocket.on('driver-location-update', (data) => {
      if (data.requestId === request._id) {
        setDriverLocation(data.location);
        setLastUpdate(new Date());
        updateDriverMarker(data.location);
      }
    });

    return () => newSocket.disconnect();
  }, [request._id]);

  useEffect(() => {
    initMap();
  }, []);

  const initMap = () => {
    if (window.google && mapRef.current) {
      const mapInstance = new window.google.maps.Map(mapRef.current, {
        zoom: 13,
        center: { lat: 33.5731, lng: -7.5898 }, // Casablanca default
        styles: [
          {
            featureType: 'poi',
            elementType: 'labels',
            stylers: [{ visibility: 'off' }]
          }
        ]
      });
      setMap(mapInstance);

      // Add pickup marker
      new window.google.maps.Marker({
        position: { 
          lat: request.pickupLocation.latitude, 
          lng: request.pickupLocation.longitude 
        },
        map: mapInstance,
        title: 'Pickup Location',
        icon: {
          url: 'data:image/svg+xml;charset=UTF-8,' + encodeURIComponent(`
            <svg width="30" height="30" viewBox="0 0 30 30" xmlns="http://www.w3.org/2000/svg">
              <circle cx="15" cy="15" r="15" fill="#4CAF50"/>
              <text x="15" y="20" text-anchor="middle" fill="white" font-size="16">üè™</text>
            </svg>
          `)
        }
      });

      // Add delivery marker
      new window.google.maps.Marker({
        position: { 
          lat: request.deliveryLocation.latitude, 
          lng: request.deliveryLocation.longitude 
        },
        map: mapInstance,
        title: 'Delivery Location',
        icon: {
          url: 'data:image/svg+xml;charset=UTF-8,' + encodeURIComponent(`
            <svg width="30" height="30" viewBox="0 0 30 30" xmlns="http://www.w3.org/2000/svg">
              <circle cx="15" cy="15" r="15" fill="#333"/>
              <text x="15" y="20" text-anchor="middle" fill="white" font-size="16">üè†</text>
            </svg>
          `)
        }
      });
    } else {
      // Fallback if Google Maps not loaded
      mapRef.current.innerHTML = `
        <div style="
          width: 100%;
          height: 100%;
          background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
          display: flex;
          align-items: center;
          justify-content: center;
          color: white;
          font-size: 18px;
          border-radius: 15px;
        ">
          üó∫Ô∏è Loading Map...
        </div>
      `;
    }
  };

  const updateDriverMarker = (location) => {
    if (map && location && location.latitude && location.longitude) {
      if (driverMarker) {
        driverMarker.setPosition({ lat: location.latitude, lng: location.longitude });
      } else {
        const marker = new window.google.maps.Marker({
          position: { lat: location.latitude, lng: location.longitude },
          map: map,
          title: 'Driver Location',
          icon: {
            url: 'data:image/svg+xml;charset=UTF-8,' + encodeURIComponent(`
              <svg width="40" height="40" viewBox="0 0 40 40" xmlns="http://www.w3.org/2000/svg">
                <circle cx="20" cy="20" r="20" fill="#FF5722" stroke="white" stroke-width="3"/>
                <text x="20" y="26" text-anchor="middle" fill="white" font-size="20">üöó</text>
              </svg>
            `)
          }
        });
        setDriverMarker(marker);
      }
      map.panTo({ lat: location.latitude, lng: location.longitude });
    }
  };

  const getStatusMessage = (status) => {
    const messages = {
      pending: 'Looking for driver...',
      accepted: 'Driver heading to store',
      shopping: 'Driver shopping for your items',
      delivering: 'Driver coming to you',
      completed: 'Delivered!',
      cancelled: 'Cancelled'
    };
    return messages[status] || status;
  };

  return (
    <div style={{ margin: '15px 0' }}>
      <div 
        ref={mapRef}
        style={{
          width: '100%',
          height: '300px',
          borderRadius: '15px',
          border: '2px solid #e0e0e0'
        }}
      />

      <div style={{
        background: '#f8f9fa',
        borderRadius: '10px',
        padding: '15px',
        marginTop: '10px'
      }}>
        <div style={{ display: 'flex', alignItems: 'center', marginBottom: '10px' }}>
          <div style={{
            fontSize: '24px',
            marginRight: '10px',
            animation: request.status === 'delivering' ? 'pulse 2s infinite' : 'none'
          }}>üöó</div>
          <h4 style={{ margin: 0, color: '#333' }}>
            {getStatusMessage(request.status)}
          </h4>
        </div>

        {driverLocation && (
          <div style={{
            background: 'white',
            borderRadius: '8px',
            padding: '10px',
            fontSize: '14px'
          }}>
            <div style={{ color: '#4CAF50', fontWeight: 'bold', marginBottom: '5px' }}>
              üìç Live Location
            </div>
            <div style={{ color: '#666', marginBottom: '5px' }}>
              {driverLocation.address || 'Getting location...'}
            </div>
            {lastUpdate && (
              <div style={{ color: '#999', fontSize: '12px' }}>
                Updated: {lastUpdate.toLocaleTimeString()}
              </div>
            )}
          </div>
        )}
      </div>

      <style>
        {`
          @keyframes pulse {
            0% { transform: scale(1); }
            50% { transform: scale(1.1); }
            100% { transform: scale(1); }
          }
        `}
      </style>
    </div>
  );
};

export default LiveTrackingMap;
