import React, { useState } from 'react';

const LocationSelector = ({ onLocationSelect, pickupLocation, deliveryLocation, mode = 'pickup' }) => {

  const handleMapClick = (event) => {
    const rect = event.currentTarget.getBoundingClientRect();
    const x = event.clientX - rect.left;
    const y = event.clientY - rect.top;

    // Convert click position to mock coordinates
    const lat = 33.5731 + (y - 125) * 0.001;
    const lng = -7.5898 + (x - 150) * 0.001;

    onLocationSelect(mode, {
      latitude: lat,
      longitude: lng,
      address: `${lat.toFixed(4)}, ${lng.toFixed(4)}`
    });
  };

  const getMarkerPosition = (location) => {
    if (!location?.latitude) return null;

    // Convert GPS coordinates to pixel position on fake map
    const x = 150 + (location.longitude + 7.5898) / 0.001;
    const y = 125 + (location.latitude - 33.5731) / 0.001;

    return { x: Math.max(10, Math.min(290, x)), y: Math.max(10, Math.min(240, y)) };
  };

  const markerPos = getMarkerPosition(pickupLocation || deliveryLocation);

  return (
    <div>
      {mode === 'home' && (
        <div style={{ marginBottom: '10px', textAlign: 'center' }}>
          <span style={{ color: '#4CAF50', fontWeight: 'bold' }}>üè† Set Home Address</span>
        </div>
      )}
      {mode === 'delivery' && (
        <div style={{ marginBottom: '10px', textAlign: 'center' }}>
          <span style={{ color: '#333', fontWeight: 'bold' }}>üìç Set Delivery Location</span>
        </div>
      )}

      <div 
        onClick={handleMapClick}
        style={{ 
          width: '100%', 
          height: '250px', 
          borderRadius: '15px',
          border: '2px solid #e0e0e0',
          background: 'linear-gradient(135deg, #f0f8f0 0%, #e8f5e8 100%)',
          position: 'relative',
          cursor: 'crosshair',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          flexDirection: 'column'
        }}
      >
        <div style={{ fontSize: '48px', marginBottom: '10px' }}>üó∫Ô∏è</div>
        <div style={{ color: '#666', textAlign: 'center' }}>
          Click to set {mode} location
        </div>

        {markerPos && (
          <div 
            style={{
              position: 'absolute',
              left: `${markerPos.x}px`,
              top: `${markerPos.y}px`,
              fontSize: '24px',
              transform: 'translate(-50%, -50%)',
              zIndex: 10,
              filter: 'drop-shadow(0 2px 4px rgba(0,0,0,0.3))'
            }}
          >
            {mode === 'home' ? 'üè†' : 'üìç'}
          </div>
        )}
      </div>

      <p style={{ fontSize: '12px', color: '#666', margin: '5px 0' }}>
        Click anywhere on the map to set {mode} location
      </p>
    </div>
  );
};

export default LocationSelector;
