import React, { useEffect, useState, useRef } from 'react';
import io from 'socket.io-client';

const LiveTrackingMap = ({ request, user }) => {
  const [driverLocation, setDriverLocation] = useState(null);
  const [lastUpdate, setLastUpdate] = useState(null);
  const [socket, setSocket] = useState(null);
  const [estimatedTime, setEstimatedTime] = useState(15);
  const mapRef = useRef(null);
  const [map, setMap] = useState(null);
  const [driverMarker, setDriverMarker] = useState(null);
  const [pickupMarker, setPickupMarker] = useState(null);
  const [deliveryMarker, setDeliveryMarker] = useState(null);

  useEffect(() => {
    const newSocket = io('http://localhost:5000');
    setSocket(newSocket);
    newSocket.emit('join-room', request._id);

    newSocket.on('driver-location-update', (data) => {
      if (data.requestId === request._id) {
        setDriverLocation(data.location);
        setLastUpdate(new Date(data.timestamp));
        updateDriverMarker(data.location);
        // Calculate rough ETA based on distance (simplified)
        if (data.location.latitude && data.location.longitude) {
          const distance = calculateDistance(
            data.location.latitude, data.location.longitude,
            request.deliveryLocation.latitude, request.deliveryLocation.longitude
          );
          setEstimatedTime(Math.max(1, Math.ceil(distance * 3))); // 3 min per km estimate
        }
      }
    });

    return () => newSocket.disconnect();
  }, [request._id]);

  useEffect(() => {
    initializeMap();
  }, []);

  const initializeMap = () => {
    if (!window.google) {
      // Load Google Maps API
      const script = document.createElement('script');
      script.src = `https://maps.googleapis.com/maps/api/js?key=AIzaSyBFw0Qbyq9zTFTd-tUY6dOWTgHz-EGE7GGABC&libraries=geometry`;
      script.async = true;
      script.defer = true;
      script.onload = () => createMap();
      document.head.appendChild(script);
    } else {
      createMap();
    }
  };

  const createMap = () => {
    if (!mapRef.current) return;

    const mapInstance = new window.google.maps.Map(mapRef.current, {
      zoom: 14,
      center: { 
        lat: request.deliveryLocation.latitude || 33.5731, 
        lng: request.deliveryLocation.longitude || -7.5898 
      },
      mapTypeId: 'roadmap',
      styles: [
        {
          featureType: 'poi.business',
          stylers: [{ visibility: 'on' }]
        },
        {
          featureType: 'transit',
          elementType: 'labels.icon',
          stylers: [{ visibility: 'off' }]
        }
      ]
    });
    setMap(mapInstance);

    // Add pickup marker
    const pickup = new window.google.maps.Marker({
      position: { 
        lat: request.pickupLocation.latitude, 
        lng: request.pickupLocation.longitude 
      },
      map: mapInstance,
      title: 'Pickup Location',
      icon: {
        url: 'data:image/svg+xml;charset=UTF-8,' + encodeURIComponent(`
          <svg width="40" height="40" viewBox="0 0 40 40" xmlns="http://www.w3.org/2000/svg">
            <circle cx="20" cy="20" r="18" fill="#4CAF50" stroke="white" stroke-width="3"/>
            <text x="20" y="26" text-anchor="middle" fill="white" font-size="16" font-weight="bold">üè™</text>
          </svg>
        `),
        scaledSize: new window.google.maps.Size(40, 40)
      }
    });
    setPickupMarker(pickup);

    // Add delivery marker
    const delivery = new window.google.maps.Marker({
      position: { 
        lat: request.deliveryLocation.latitude, 
        lng: request.deliveryLocation.longitude 
      },
      map: mapInstance,
      title: 'Delivery Location',
      icon: {
        url: 'data:image/svg+xml;charset=UTF-8,' + encodeURIComponent(`
          <svg width="40" height="40" viewBox="0 0 40 40" xmlns="http://www.w3.org/2000/svg">
            <circle cx="20" cy="20" r="18" fill="#333" stroke="white" stroke-width="3"/>
            <text x="20" y="26" text-anchor="middle" fill="white" font-size="16" font-weight="bold">üè†</text>
          </svg>
        `),
        scaledSize: new window.google.maps.Size(40, 40)
      }
    });
    setDeliveryMarker(delivery);

    // Fit bounds to show all markers
    const bounds = new window.google.maps.LatLngBounds();
    bounds.extend({ lat: request.pickupLocation.latitude, lng: request.pickupLocation.longitude });
    bounds.extend({ lat: request.deliveryLocation.latitude, lng: request.deliveryLocation.longitude });
    mapInstance.fitBounds(bounds);
  };

  const updateDriverMarker = (location) => {
    if (!map || !location || !location.latitude || !location.longitude) return;

    if (driverMarker) {
      // Update existing marker position
      driverMarker.setPosition({ lat: location.latitude, lng: location.longitude });
    } else {
      // Create new driver marker
      const marker = new window.google.maps.Marker({
        position: { lat: location.latitude, lng: location.longitude },
        map: map,
        title: 'Driver Location',
        icon: {
          url: 'data:image/svg+xml;charset=UTF-8,' + encodeURIComponent(`
            <svg width="50" height="50" viewBox="0 0 50 50" xmlns="http://www.w3.org/2000/svg">
              <circle cx="25" cy="25" r="23" fill="#FF5722" stroke="white" stroke-width="4"/>
              <text x="25" y="32" text-anchor="middle" fill="white" font-size="20" font-weight="bold">üöó</text>
            </svg>
          `),
          scaledSize: new window.google.maps.Size(50, 50)
        },
        zIndex: 1000
      });
      setDriverMarker(marker);
    }

    // Center map on driver with smooth animation
    map.panTo({ lat: location.latitude, lng: location.longitude });
  };

  const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371; // Earth's radius in km
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a = Math.sin(dLat/2) * Math.sin(dLat/2) +
      Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
      Math.sin(dLon/2) * Math.sin(dLon/2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
    return R * c;
  };

  const getStatusMessage = (status) => {
    const messages = {
      pending: 'Looking for driver...',
      accepted: 'Driver heading to store',
      shopping: 'Driver shopping for your items',
      delivering: 'Driver coming to you',
      completed: 'Delivered!',
      cancelled: 'Cancelled'
    };
    return messages[status] || status;
  };

  return (
    <div style={{ margin: '15px 0' }}>
      {/* Real Google Maps */}
      <div style={{ position: 'relative' }}>
        <div 
          ref={mapRef}
          style={{
            width: '100%',
            height: '300px',
            borderRadius: '15px',
            border: '2px solid #e0e0e0',
            overflow: 'hidden'
          }}
        />

        {/* Status Overlay */}
        <div style={{
          position: 'absolute',
          top: '15px',
          left: '15px',
          background: 'rgba(255,255,255,0.95)',
          padding: '8px 12px',
          borderRadius: '20px',
          fontSize: '12px',
          fontWeight: 'bold',
          color: '#333',
          boxShadow: '0 2px 10px rgba(0,0,0,0.2)',
          zIndex: 1000
        }}>
          üöó {getStatusMessage(request.status)}
        </div>

        {/* ETA Badge */}
        <div style={{
          position: 'absolute',
          top: '15px',
          right: '15px',
          background: '#4CAF50',
          color: 'white',
          padding: '8px 12px',
          borderRadius: '20px',
          fontSize: '12px',
          fontWeight: 'bold',
          boxShadow: '0 2px 10px rgba(76,175,80,0.3)',
          zIndex: 1000
        }}>
          ETA: {Math.ceil(estimatedTime)}min
        </div>

        {/* Driver Status */}
        {driverLocation && (
          <div style={{
            position: 'absolute',
            bottom: '15px',
            left: '15px',
            right: '15px',
            background: 'rgba(255,87,34,0.95)',
            color: 'white',
            padding: '10px',
            borderRadius: '10px',
            fontSize: '12px',
            boxShadow: '0 2px 10px rgba(0,0,0,0.2)',
            zIndex: 1000
          }}>
            <div style={{ fontWeight: 'bold', marginBottom: '3px' }}>
              üìç Driver Live Location
            </div>
            <div style={{ opacity: 0.9 }}>
              {driverLocation.latitude.toFixed(4)}, {driverLocation.longitude.toFixed(4)}
            </div>
            {lastUpdate && (
              <div style={{ opacity: 0.8, fontSize: '10px', marginTop: '3px' }}>
                Updated: {lastUpdate.toLocaleTimeString()}
              </div>
            )}
          </div>
        )}

        {!driverLocation && (
          <div style={{
            position: 'absolute',
            bottom: '15px',
            left: '15px',
            right: '15px',
            background: 'rgba(0,0,0,0.7)',
            color: 'white',
            padding: '10px',
            borderRadius: '10px',
            fontSize: '12px',
            textAlign: 'center',
            zIndex: 1000
          }}>
            üìç Waiting for driver to share location...
          </div>
        )}
      </div>

      {/* Live Tracking Info */}
      <div style={{
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        borderRadius: '12px',
        padding: '15px',
        color: 'white',
        marginTop: '15px'
      }}>
        <div style={{ display: 'flex', alignItems: 'center', marginBottom: '10px' }}>
          <div style={{
            width: '12px',
            height: '12px',
            background: '#4CAF50',
            borderRadius: '50%',
            marginRight: '8px',
            animation: 'pulse 2s infinite'
          }} />
          <span style={{ fontSize: '14px', fontWeight: 'bold' }}>Live Location</span>
        </div>

        {driverLocation ? (
          <>
            <div style={{ fontSize: '16px', marginBottom: '8px' }}>
              üìç {driverLocation.address || 'Live location'}
            </div>

            <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '11px', opacity: 0.8 }}>
              <span>{driverLocation.latitude.toFixed(4)}, {driverLocation.longitude.toFixed(4)}</span>
              <span>Updated: {lastUpdate?.toLocaleTimeString()}</span>
            </div>
          </>
        ) : (
          <div style={{ fontSize: '14px', opacity: 0.8 }}>
            Waiting for driver to share location...
          </div>
        )}
      </div>

      <style>
        {`
          @keyframes pulse {
            0%, 100% { opacity: 1; }
            50% { opacity: 0.5; }
          }
        `}
      </style>
    </div>
  );
};

export default LiveTrackingMap;
