import React, { useState } from 'react';

const LocationSelector = ({ onLocationSelect, pickupLocation, deliveryLocation }) => {
  const [mode, setMode] = useState('pickup');

  const handleMapClick = (event) => {
    const rect = event.currentTarget.getBoundingClientRect();
    const x = event.clientX - rect.left;
    const y = event.clientY - rect.top;

    // Convert click position to mock coordinates
    const lat = 33.5731 + (y - 125) * 0.001;
    const lng = -7.5898 + (x - 150) * 0.001;

    onLocationSelect(mode, {
      latitude: lat,
      longitude: lng,
      address: `${lat.toFixed(4)}, ${lng.toFixed(4)}`
    });
  };

  return (
    <div>
      <div style={{ display: 'flex', gap: '10px', marginBottom: '10px' }}>
        <button
          type="button"
          onClick={() => setMode('pickup')}
          style={{
            padding: '8px 16px',
            border: 'none',
            borderRadius: '20px',
            background: mode === 'pickup' ? '#4CAF50' : '#e0e0e0',
            color: mode === 'pickup' ? 'white' : '#333',
            cursor: 'pointer'
          }}
        >
          üü¢ Set Pickup
        </button>
        <button
          type="button"
          onClick={() => setMode('delivery')}
          style={{
            padding: '8px 16px',
            border: 'none',
            borderRadius: '20px',
            background: mode === 'delivery' ? '#333' : '#e0e0e0',
            color: mode === 'delivery' ? 'white' : '#333',
            cursor: 'pointer'
          }}
        >
          ‚ö´ Set Delivery
        </button>
      </div>

      <div 
        onClick={handleMapClick}
        style={{ 
          width: '100%', 
          height: '250px', 
          borderRadius: '15px',
          border: '2px solid #e0e0e0',
          background: 'linear-gradient(135deg, #f0f8f0 0%, #e8f5e8 100%)',
          position: 'relative',
          cursor: 'crosshair',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          flexDirection: 'column'
        }}
      >
        <div style={{ fontSize: '48px', marginBottom: '10px' }}>üó∫Ô∏è</div>
        <div style={{ color: '#666', textAlign: 'center' }}>
          Click to set {mode} location
        </div>

        {pickupLocation?.latitude && (
          <div 
            style={{
              position: 'absolute',
              left: '20%',
              top: '30%',
              fontSize: '20px'
            }}
          >
            üü¢
          </div>
        )}

        {deliveryLocation?.latitude && (
          <div 
            style={{
              position: 'absolute',
              right: '20%',
              bottom: '30%',
              fontSize: '20px'
            }}
          >
            ‚ö´
          </div>
        )}
      </div>

      <p style={{ fontSize: '12px', color: '#666', margin: '5px 0' }}>
        Click anywhere on the map to set {mode} location
      </p>
    </div>
  );
};

export default LocationSelector;
