import React, { useEffect, useState, useRef } from 'react';
import io from 'socket.io-client';

const LiveTrackingMap = ({ request, user }) => {
  const [driverLocation, setDriverLocation] = useState(null);
  const [lastUpdate, setLastUpdate] = useState(null);
  const [socket, setSocket] = useState(null);
  const [estimatedTime, setEstimatedTime] = useState(15);
  const [routePath, setRoutePath] = useState(null);
  const mapRef = useRef(null);
  const [map, setMap] = useState(null);
  const [driverMarker, setDriverMarker] = useState(null);

  useEffect(() => {
    const newSocket = io('http://192.168.11.106:5000');
    setSocket(newSocket);
    newSocket.emit('join-room', request._id);

    newSocket.on('driver-location-update', (data) => {
      if (data.requestId === request._id) {
        console.log('üìç Received driver location:', data.location);
        setDriverLocation(data.location);
        setLastUpdate(new Date(data.timestamp));

        // Use ML-calculated ETA from backend
        if (data.eta) {
          setEstimatedTime(data.eta);
          console.log('ü§ñ ML-ETA received:', data.eta, 'minutes');
        }

        if (!map && window.google && mapRef.current) {
          initGlovoStyleMap(data.location);
        } else if (map) {
          updateDriverMarker(data.location);
        }
      }
    });

    return () => newSocket.disconnect();
  }, [request._id, map]);

  const initGlovoStyleMap = (driverLoc) => {
    if (!window.google || !mapRef.current) return;

    console.log('üó∫Ô∏è Creating Glovo-style map...');

    // Custom Glovo-like map styling
    const glovoMapStyle = [
      {
        "featureType": "all",
        "elementType": "geometry",
        "stylers": [{"color": "#f5f5f5"}]
      },
      {
        "featureType": "road",
        "elementType": "geometry",
        "stylers": [{"color": "#ffffff"}]
      },
      {
        "featureType": "road",
        "elementType": "geometry.stroke",
        "stylers": [{"color": "#e8e8e8"}, {"weight": 1}]
      },
      {
        "featureType": "road.highway",
        "elementType": "geometry",
        "stylers": [{"color": "#ffd700"}]
      },
      {
        "featureType": "water",
        "elementType": "geometry",
        "stylers": [{"color": "#c9d6ff"}]
      },
      {
        "featureType": "poi",
        "stylers": [{"visibility": "off"}]
      },
      {
        "featureType": "transit",
        "stylers": [{"visibility": "off"}]
      },
      {
        "featureType": "administrative",
        "elementType": "labels",
        "stylers": [{"visibility": "simplified"}]
      },
      {
        "featureType": "road",
        "elementType": "labels",
        "stylers": [{"visibility": "simplified"}]
      }
    ];

    const mapInstance = new window.google.maps.Map(mapRef.current, {
      zoom: 16,
      center: { lat: driverLoc.latitude, lng: driverLoc.longitude },
      styles: glovoMapStyle,
      disableDefaultUI: true,
      zoomControl: false,
      streetViewControl: false,
      fullscreenControl: false,
      mapTypeControl: false,
      gestureHandling: 'cooperative'
    });

    setMap(mapInstance);

    // Create custom driver marker (Glovo-style)
    const driverIcon = {
      path: 'M12 2C8.13 2 5 5.13 5 9c0 5.25 7 13 7 13s7-7.75 7-13c0-3.87-3.13-7-7-7z',
      fillColor: '#00D4AA',
      fillOpacity: 1,
      strokeColor: '#ffffff',
      strokeWeight: 3,
      scale: 1.5,
      anchor: new window.google.maps.Point(12, 24)
    };

    const marker = new window.google.maps.Marker({
      position: { lat: driverLoc.latitude, lng: driverLoc.longitude },
      map: mapInstance,
      icon: driverIcon,
      title: 'Your Driver',
      zIndex: 1000
    });

    setDriverMarker(marker);

    // Add delivery location marker
    const deliveryIcon = {
      path: 'M12 2l3.09 6.26L22 9.27l-5 4.87 1.18 6.88L12 17.77l-6.18 3.25L7 14.14 2 9.27l6.91-1.01L12 2z',
      fillColor: '#FF6B6B',
      fillOpacity: 1,
      strokeColor: '#ffffff',
      strokeWeight: 2,
      scale: 1.2,
      anchor: new window.google.maps.Point(12, 12)
    };

    new window.google.maps.Marker({
      position: { lat: request.deliveryLocation.latitude, lng: request.deliveryLocation.longitude },
      map: mapInstance,
      icon: deliveryIcon,
      title: 'Delivery Location'
    });

    // Draw route path (simulated)
    drawRoutePath(mapInstance, driverLoc);

    console.log('‚úÖ Glovo-style map created!');
  };

  const drawRoutePath = (mapInstance, driverLoc) => {
    const routeCoordinates = [
      { lat: driverLoc.latitude, lng: driverLoc.longitude },
      { lat: request.deliveryLocation.latitude, lng: request.deliveryLocation.longitude }
    ];

    const routeLine = new window.google.maps.Polyline({
      path: routeCoordinates,
      geodesic: true,
      strokeColor: '#00D4AA',
      strokeOpacity: 1.0,
      strokeWeight: 4
    });

    routeLine.setMap(mapInstance);
    setRoutePath(routeLine);
  };

  const updateDriverMarker = (location) => {
    if (!map || !driverMarker) return;

    console.log('üöó Moving driver to:', location.latitude, location.longitude);

    // Smooth animation
    const newPos = { lat: location.latitude, lng: location.longitude };
    driverMarker.setPosition(newPos);

    // Update route path
    if (routePath) {
      const newPath = [
        newPos,
        { lat: request.deliveryLocation.latitude, lng: request.deliveryLocation.longitude }
      ];
      routePath.setPath(newPath);
    }

    // Smart camera follow
    map.panTo(newPos);

    // Calculate ETA with ML-like logic
    const distance = calculateDistance(
      location.latitude, location.longitude,
      request.deliveryLocation.latitude, request.deliveryLocation.longitude
    );

    // Advanced ETA calculation (simulating ML)
    if (distance < 1) {
      // Same location or very close
      const eta = Math.floor(Math.random() * 3) + 2; // 2-5 minutes
      setEstimatedTime(eta);
    } else {
      const baseSpeed = 25; // km/h
      const trafficFactor = 1.2; // simulate traffic
      const eta = Math.max(5, Math.min(45, Math.ceil((distance / baseSpeed) * 60 * trafficFactor)));
      setEstimatedTime(eta);
    }
  };

  const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371;
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a = Math.sin(dLat/2) * Math.sin(dLat/2) +
      Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
      Math.sin(dLon/2) * Math.sin(dLon/2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
    return R * c;
  };

  const getStatusMessage = (status) => {
    const messages = {
      pending: 'Looking for your driver...',
      accepted: 'Driver is on the way',
      shopping: 'Picking up your order',
      delivering: 'On the way to you',
      completed: 'Delivered!',
      cancelled: 'Cancelled'
    };
    return messages[status] || status;
  };

  return (
    <div style={{ margin: '0', position: 'relative' }}>
      {/* Glovo-style Map Container */}
      <div 
        ref={mapRef}
        style={{
          width: '100%',
          height: '400px',
          borderRadius: '0',
          border: 'none',
          overflow: 'hidden'
        }}
      />

      {/* Custom Glovo-style UI Overlay */}
      <div style={{
        position: 'absolute',
        top: '20px',
        left: '20px',
        right: '20px',
        background: 'rgba(255,255,255,0.95)',
        borderRadius: '12px',
        padding: '16px',
        boxShadow: '0 4px 20px rgba(0,0,0,0.1)',
        zIndex: 1000
      }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <div>
            <div style={{ fontSize: '16px', fontWeight: 'bold', color: '#333', marginBottom: '4px' }}>
              {getStatusMessage(request.status)}
            </div>
            <div style={{ fontSize: '14px', color: '#666' }}>
              {request.driverId?.name || 'Driver'} ‚Ä¢ ‚≠ê {request.driverId?.rating?.toFixed(1) || '5.0'}
            </div>
          </div>
          <div style={{ textAlign: 'right' }}>
            <div style={{ fontSize: '24px', fontWeight: 'bold', color: '#00D4AA' }}>
              {estimatedTime}min
            </div>
            <div style={{ fontSize: '12px', color: '#666' }}>
              ETA
            </div>
          </div>
        </div>
      </div>

      {/* Live Status Bar */}
      {driverLocation && (
        <div style={{
          position: 'absolute',
          bottom: '20px',
          left: '20px',
          right: '20px',
          background: '#00D4AA',
          color: 'white',
          padding: '16px',
          borderRadius: '12px',
          textAlign: 'center',
          boxShadow: '0 4px 20px rgba(0,212,170,0.3)',
          zIndex: 1000
        }}>
          <div style={{ fontSize: '14px', fontWeight: 'bold', marginBottom: '4px' }}>
            üöó Live Tracking Active
          </div>
          <div style={{ fontSize: '12px', opacity: 0.9 }}>
            Last update: {lastUpdate?.toLocaleTimeString()}
          </div>
        </div>
      )}

      {!driverLocation && (
        <div style={{
          position: 'absolute',
          bottom: '20px',
          left: '20px',
          right: '20px',
          background: 'rgba(0,0,0,0.8)',
          color: 'white',
          padding: '16px',
          borderRadius: '12px',
          textAlign: 'center',
          zIndex: 1000
        }}>
          <div style={{ fontSize: '14px' }}>
            üìç Waiting for driver location...
          </div>
        </div>
      )}

      {/* Custom Zoom Controls */}
      <div style={{
        position: 'absolute',
        right: '20px',
        top: '100px',
        display: 'flex',
        flexDirection: 'column',
        gap: '8px',
        zIndex: 1000
      }}>
        <button 
          onClick={() => map && map.setZoom(map.getZoom() + 1)}
          style={{
            width: '40px',
            height: '40px',
            borderRadius: '50%',
            border: 'none',
            background: 'white',
            boxShadow: '0 2px 10px rgba(0,0,0,0.2)',
            fontSize: '18px',
            cursor: 'pointer'
          }}
        >+</button>
        <button 
          onClick={() => map && map.setZoom(map.getZoom() - 1)}
          style={{
            width: '40px',
            height: '40px',
            borderRadius: '50%',
            border: 'none',
            background: 'white',
            boxShadow: '0 2px 10px rgba(0,0,0,0.2)',
            fontSize: '18px',
            cursor: 'pointer'
          }}
        >-</button>
      </div>
    </div>
  );
};

export default LiveTrackingMap;
