import React, { useEffect, useState, useRef } from 'react';
import io from 'socket.io-client';

const LiveTrackingMap = ({ request, user }) => {
  const [driverLocation, setDriverLocation] = useState(null);
  const [lastUpdate, setLastUpdate] = useState(null);
  const [socket, setSocket] = useState(null);
  const [estimatedTime, setEstimatedTime] = useState(15);
  const mapRef = useRef(null);
  const [map, setMap] = useState(null);
  const [driverMarker, setDriverMarker] = useState(null);
  const [deliveryMarker, setDeliveryMarker] = useState(null);

  useEffect(() => {
    const newSocket = io('http://localhost:5000');
    setSocket(newSocket);
    newSocket.emit('join-room', request._id);

    newSocket.on('driver-location-update', (data) => {
      if (data.requestId === request._id) {
        console.log('üìç Received driver location:', data.location);
        setDriverLocation(data.location);
        setLastUpdate(new Date(data.timestamp));

        // Use ML-calculated ETA from backend
        if (data.eta) {
          setEstimatedTime(data.eta);
          console.log('ü§ñ ML-ETA received:', data.eta, 'minutes');
        }

        if (!map && window.L && mapRef.current) {
          initMap(data.location);
        } else if (map) {
          updateDriverMarker(data.location);
        }
      }
    });

    return () => newSocket.disconnect();
  }, [request._id, map]);

  const initMap = (driverLoc) => {
    if (!window.L || !mapRef.current) return;

    console.log('üó∫Ô∏è Creating tracking map...');

    const leafletMap = window.L.map(mapRef.current, {
      zoomControl: false,
      attributionControl: false
    }).setView([driverLoc.latitude, driverLoc.longitude], 16);

    window.L.tileLayer('https://mt1.google.com/vt/lyrs=r&x={x}&y={y}&z={z}', {
      attribution: '',
      maxZoom: 20
    }).addTo(leafletMap);

    setMap(leafletMap);

    // Add driver marker
    const driverIcon = window.L.divIcon({
      html: `
        <div style="
          width: 40px;
          height: 40px;
          background: #00D084;
          border-radius: 50%;
          border: 3px solid white;
          box-shadow: 0 4px 12px rgba(0, 208, 132, 0.4);
          display: flex;
          align-items: center;
          justify-content: center;
          font-size: 20px;
        ">üõµ</div>
      `,
      className: 'driver-marker',
      iconSize: [40, 40],
      iconAnchor: [20, 20]
    });

    const marker = window.L.marker([driverLoc.latitude, driverLoc.longitude], {
      icon: driverIcon
    }).addTo(leafletMap);

    setDriverMarker(marker);

    // Add delivery location marker
    const deliveryIcon = window.L.divIcon({
      html: `
        <div style="
          width: 36px;
          height: 36px;
          background: #FF6B35;
          border-radius: 50%;
          border: 3px solid white;
          box-shadow: 0 4px 12px rgba(255, 107, 53, 0.4);
          display: flex;
          align-items: center;
          justify-content: center;
          font-size: 18px;
        ">üè†</div>
      `,
      className: 'delivery-marker',
      iconSize: [36, 36],
      iconAnchor: [18, 18]
    });

    const deliveryMarkerInstance = window.L.marker([request.deliveryLocation.latitude, request.deliveryLocation.longitude], {
      icon: deliveryIcon
    }).addTo(leafletMap);

    setDeliveryMarker(deliveryMarkerInstance);

    console.log('‚úÖ Tracking map created!');
  };



  const updateDriverMarker = (location) => {
    if (!map || !driverMarker) return;

    console.log('üöó Moving driver to:', location.latitude, location.longitude);

    // Update driver position
    driverMarker.setLatLng([location.latitude, location.longitude]);

    // Center map on driver
    map.setView([location.latitude, location.longitude], 16);

    // Calculate ETA
    const distance = calculateDistance(
      location.latitude, location.longitude,
      request.deliveryLocation.latitude, request.deliveryLocation.longitude
    );

    if (distance < 1) {
      const eta = Math.floor(Math.random() * 3) + 2;
      setEstimatedTime(eta);
    } else {
      const baseSpeed = 25;
      const trafficFactor = 1.2;
      const eta = Math.max(5, Math.min(45, Math.ceil((distance / baseSpeed) * 60 * trafficFactor)));
      setEstimatedTime(eta);
    }
  };

  const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371;
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a = Math.sin(dLat/2) * Math.sin(dLat/2) +
      Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
      Math.sin(dLon/2) * Math.sin(dLon/2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
    return R * c;
  };

  const getStatusMessage = (status) => {
    const messages = {
      pending: 'Looking for your driver...',
      accepted: 'Driver is on the way',
      shopping: 'Picking up your order',
      delivering: 'On the way to you',
      completed: 'Delivered!',
      cancelled: 'Cancelled'
    };
    return messages[status] || status;
  };

  return (
    <div style={{ margin: '0', position: 'relative' }}>
      {/* Glovo-style Map Container */}
      <div 
        ref={mapRef}
        style={{
          width: '100%',
          height: '60vh',
          borderRadius: '20px',
          border: 'none',
          overflow: 'hidden',
          boxShadow: '0 4px 20px rgba(0,0,0,0.1)'
        }}
      />

      {/* Custom Glovo-style UI Overlay */}
      <div style={{
        position: 'absolute',
        top: '20px',
        left: '20px',
        right: '20px',
        background: 'rgba(255,255,255,0.95)',
        borderRadius: '12px',
        padding: '16px',
        boxShadow: '0 4px 20px rgba(0,0,0,0.1)',
        zIndex: 1000
      }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <div>
            <div style={{ fontSize: '16px', fontWeight: 'bold', color: '#333', marginBottom: '4px' }}>
              {getStatusMessage(request.status)}
            </div>
            <div style={{ fontSize: '14px', color: '#666' }}>
              {request.driverId?.name || 'Driver'} ‚Ä¢ ‚≠ê {request.driverId?.rating?.toFixed(1) || '5.0'}
            </div>
          </div>
          <div style={{ textAlign: 'right' }}>
            <div style={{ fontSize: '24px', fontWeight: 'bold', color: '#00D4AA' }}>
              {estimatedTime}min
            </div>
            <div style={{ fontSize: '12px', color: '#666' }}>
              ETA
            </div>
          </div>
        </div>
      </div>

      {/* Live Status Bar */}
      {driverLocation && (
        <div style={{
          position: 'absolute',
          bottom: '20px',
          left: '20px',
          right: '20px',
          background: '#00D4AA',
          color: 'white',
          padding: '16px',
          borderRadius: '12px',
          textAlign: 'center',
          boxShadow: '0 4px 20px rgba(0,212,170,0.3)',
          zIndex: 1000
        }}>
          <div style={{ fontSize: '14px', fontWeight: 'bold', marginBottom: '4px' }}>
            üöó Live Tracking Active
          </div>
          <div style={{ fontSize: '12px', opacity: 0.9 }}>
            Last update: {lastUpdate?.toLocaleTimeString()}
          </div>
        </div>
      )}

      {!driverLocation && (
        <div style={{
          position: 'absolute',
          bottom: '20px',
          left: '20px',
          right: '20px',
          background: 'rgba(0,0,0,0.8)',
          color: 'white',
          padding: '16px',
          borderRadius: '12px',
          textAlign: 'center',
          zIndex: 1000
        }}>
          <div style={{ fontSize: '14px' }}>
            üìç Waiting for driver location...
          </div>
        </div>
      )}


    </div>
  );
};

export default LiveTrackingMap;
