import axios from 'axios';

const API_URL = 'http://192.168.1.227:5000/api';

export class LocationTracker {
  constructor(userId) {
    this.userId = userId;
    this.watchId = null;
    this.isTracking = false;
  }

  async startTracking() {
    this.isTracking = true;

    // Get driver's actual location from database first
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`${API_URL}/users/${this.userId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      const userData = await response.json();

      let baseLocation;
      if (userData.location?.latitude && userData.location?.longitude) {
        // Use driver's stored location
        baseLocation = {
          latitude: userData.location.latitude,
          longitude: userData.location.longitude
        };
        console.log('Using driver stored location:', baseLocation);
      } else {
        // Fallback to Casablanca
        baseLocation = {
          latitude: 33.5731,
          longitude: -7.5898
        };
        console.log('Using default Casablanca location');
      }

      // Update location immediately
      this.updateLocation(baseLocation.latitude, baseLocation.longitude);

      // Simulate small movements every 10 seconds
      this.watchId = setInterval(() => {
        const newLat = baseLocation.latitude + (Math.random() - 0.5) * 0.001;
        const newLng = baseLocation.longitude + (Math.random() - 0.5) * 0.001;
        this.updateLocation(newLat, newLng);
      }, 10000);

    } catch (error) {
      console.error('Error getting driver location:', error);
    }
  }

  stopTracking() {
    if (this.watchId) {
      clearInterval(this.watchId);
      this.watchId = null;
    }
    this.isTracking = false;
    console.log('Mock location tracking stopped');
  }

  async updateLocation(latitude, longitude) {
    try {
      const token = localStorage.getItem('token');
      await axios.patch(`${API_URL}/users/${this.userId}/location`, {
        latitude,
        longitude,
        address: `${latitude.toFixed(4)}, ${longitude.toFixed(4)}`
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });

      // Cache location and broadcast to customers via socket
      if (window.driverSocket) {
        const locationData = {
          driverId: this.userId,
          location: { latitude, longitude },
          timestamp: Date.now()
        };

        window.driverSocket.emit('driver-location', locationData);
        window.driverSocket.emit('cache-driver-location', locationData);
      }
    } catch (error) {
      console.error('Failed to update location:', error);
    }
  }

  async getCurrentLocation() {
    // Try real geolocation first, fallback to mock
    return new Promise((resolve) => {
      if (navigator.geolocation && window.location.protocol === 'https:') {
        navigator.geolocation.getCurrentPosition(
          (position) => {
            const realLocation = {
              latitude: position.coords.latitude,
              longitude: position.coords.longitude
            };
            console.log('Using real GPS location:', realLocation);
            resolve(realLocation);
          },
          (error) => {
            console.log('GPS failed, using mock location:', error.message);
            const mockLocation = {
              latitude: 33.5731 + (Math.random() - 0.5) * 0.01,
              longitude: -7.5898 + (Math.random() - 0.5) * 0.01
            };
            resolve(mockLocation);
          },
          { enableHighAccuracy: true, timeout: 10000 }
        );
      } else {
        console.log('HTTPS required for GPS, using mock location');
        const mockLocation = {
          latitude: 33.5731 + (Math.random() - 0.5) * 0.01,
          longitude: -7.5898 + (Math.random() - 0.5) * 0.01
        };
        resolve(mockLocation);
      }
    });
  }
}

export default LocationTracker;
