import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import io from 'socket.io-client';

const API_URL = 'http://192.168.1.227:5000/api';

function CustomerDashboard({ user }) {
  const [requests, setRequests] = useState([]);
  const [availableDrivers, setAvailableDrivers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('home');
  const mapRef = useRef(null);
  const [map, setMap] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    loadMyRequests();
    loadAvailableDrivers();
    initMap();

    const socket = io('http://192.168.1.227:5000');
    socket.on('driver-locations', (drivers) => {
      setAvailableDrivers(drivers);
      updateDriversOnMap(drivers);
    });

    return () => socket.disconnect();
  }, []);

  const loadMyRequests = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`${API_URL}/requests/my/${user._id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setRequests(response.data);
    } catch (error) {
      console.error('Error loading requests:', error);
    }
    setLoading(false);
  };

  const loadAvailableDrivers = async () => {
    try {
      const response = await axios.get(`${API_URL}/drivers/available`);
      setAvailableDrivers(response.data);
    } catch (error) {
      console.error('Error loading drivers:', error);
    }
  };

  const initMap = () => {
    if (!window.google || !mapRef.current) return;

    const mapInstance = new window.google.maps.Map(mapRef.current, {
      zoom: 14,
      center: { lat: 33.5731, lng: -7.5898 },
      styles: [
        { featureType: "poi", stylers: [{ visibility: "off" }] },
        { featureType: "transit", stylers: [{ visibility: "off" }] }
      ],
      disableDefaultUI: true
    });

    setMap(mapInstance);
  };

  const updateDriversOnMap = (drivers) => {
    if (!map) return;

    drivers.forEach(driver => {
      if (driver.currentLocation) {
        new window.google.maps.Marker({
          position: {
            lat: driver.currentLocation.latitude,
            lng: driver.currentLocation.longitude
          },
          map: map,
          icon: {
            path: 'M12 2C8.13 2 5 5.13 5 9c0 5.25 7 13 7 13s7-7.75 7-13c0-3.87-3.13-7-7-7z',
            fillColor: '#28a745',
            fillOpacity: 1,
            strokeColor: '#ffffff',
            strokeWeight: 2,
            scale: 1.2
          }
        });
      }
    });
  };

  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    window.location.href = '/login';
  };

  if (activeTab === 'drivers') {
    return (
      <div className="container">
        <div className="indrive-header">
          <div className="header-top">
            <button className="menu-btn" onClick={() => setActiveTab('home')}>‚Üê</button>
            <h2>Available Drivers</h2>
            <div></div>
          </div>
        </div>

        <div className="driver-list">
          {availableDrivers.map(driver => (
            <div key={driver._id} className="driver-item">
              <div className="driver-avatar">
                {driver.name?.charAt(0) || 'D'}
              </div>
              <div className="driver-info">
                <div className="driver-name">{driver.name || 'Unknown Driver'}</div>
                <div className="driver-rating">‚≠ê {driver.rating?.toFixed(1) || '5.0'} ({driver.totalReviews || 0} reviews)</div>
                <div className="driver-vehicle">{driver.vehicle?.type} {driver.vehicle?.model}</div>
              </div>
              <div className="driver-status">
                <div className="online-indicator">üü¢ Online</div>
                <div className="driver-distance">2 min away</div>
              </div>
            </div>
          ))}
        </div>

        <div className="bottom-nav">
          <button className="nav-item" onClick={() => setActiveTab('home')}>
            <div className="nav-icon">üè†</div>
            <div>Home</div>
          </button>
          <button className="nav-item active">
            <div className="nav-icon">üë•</div>
            <div>Drivers</div>
          </button>
          <button className="nav-item" onClick={() => setActiveTab('orders')}>
            <div className="nav-icon">üì¶</div>
            <div>Orders</div>
          </button>
          <button className="nav-item" onClick={logout}>
            <div className="nav-icon">üë§</div>
            <div>Profile</div>
          </button>
        </div>
      </div>
    );
  }

  if (activeTab === 'orders') {
    return (
      <div className="container">
        <div className="indrive-header">
          <div className="header-top">
            <button className="menu-btn" onClick={() => setActiveTab('home')}>‚Üê</button>
            <h2>My Orders</h2>
            <div></div>
          </div>
        </div>

        <div className="recent-orders">
          {requests.map(request => (
            <div key={request._id} className="order-item" onClick={() => navigate(`/track/${request._id}`)}>
              <div className="order-header">
                <div>
                  <div className="order-title">{request.item}</div>
                  <div className="order-store">from {request.store}</div>
                </div>
                <div className={`order-status status-${request.status}`}>
                  {request.status}
                </div>
              </div>
              <div className="order-price">{request.price} MAD</div>
            </div>
          ))}
        </div>

        <div className="bottom-nav">
          <button className="nav-item" onClick={() => setActiveTab('home')}>
            <div className="nav-icon">üè†</div>
            <div>Home</div>
          </button>
          <button className="nav-item" onClick={() => setActiveTab('drivers')}>
            <div className="nav-icon">üë•</div>
            <div>Drivers</div>
          </button>
          <button className="nav-item active">
            <div className="nav-icon">üì¶</div>
            <div>Orders</div>
          </button>
          <button className="nav-item" onClick={logout}>
            <div className="nav-icon">üë§</div>
            <div>Profile</div>
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      {/* inDrive Header */}
      <div className="indrive-header">
        <div className="header-top">
          <button className="menu-btn">‚ò∞</button>
          <button className="profile-btn" onClick={logout}>
            {user.name?.charAt(0) || 'U'}
          </button>
        </div>

        <div className="location-section">
          <div className="location-label">Your location</div>
          <div className="location-text">
            üìç Casablanca, Morocco
          </div>
        </div>
      </div>

      {/* Map */}
      <div className="map-section">
        <div className="map-overlay">
          <div>üìç Find drivers nearby</div>
          <div className="drivers-online">{availableDrivers.length} online</div>
        </div>
        <div ref={mapRef} style={{ width: '100%', height: '100%' }} />
      </div>

      {/* Action Buttons */}
      <div className="action-buttons">
        <button className="main-action" onClick={() => navigate('/create-request')}>
          üöÄ Request Delivery
        </button>

        <div className="secondary-actions">
          <button className="secondary-btn" onClick={() => setActiveTab('drivers')}>
            <span className="icon">üë•</span>
            <div>Drivers</div>
            <div style={{ fontSize: '11px', color: '#666' }}>{availableDrivers.length} available</div>
          </button>
          <button className="secondary-btn" onClick={() => navigate('/drivers-map')}>
            <span className="icon">üó∫Ô∏è</span>
            <div>Map View</div>
            <div style={{ fontSize: '11px', color: '#666' }}>Live tracking</div>
          </button>
        </div>
      </div>

      {/* Recent Orders */}
      <div className="recent-orders">
        <div className="section-title">Recent orders</div>

        {loading ? (
          <div style={{ textAlign: 'center', padding: '20px' }}>Loading...</div>
        ) : requests.length === 0 ? (
          <div className="empty-state">
            <div className="empty-icon">üì¶</div>
            <div>No orders yet</div>
            <div style={{ fontSize: '14px', marginTop: '8px' }}>Your delivery history will appear here</div>
          </div>
        ) : (
          requests.slice(0, 3).map(request => (
            <div key={request._id} className="order-item" onClick={() => navigate(`/track/${request._id}`)}>
              <div className="order-header">
                <div>
                  <div className="order-title">{request.item}</div>
                  <div className="order-store">from {request.store}</div>
                </div>
                <div className={`order-status status-${request.status}`}>
                  {request.status}
                </div>
              </div>
              <div className="order-price">{request.price} MAD</div>
            </div>
          ))
        )}
      </div>

      {/* Bottom Navigation */}
      <div className="bottom-nav">
        <button className="nav-item active">
          <div className="nav-icon">üè†</div>
          <div>Home</div>
        </button>
        <button className="nav-item" onClick={() => setActiveTab('drivers')}>
          <div className="nav-icon">üë•</div>
          <div>Drivers</div>
        </button>
        <button className="nav-item" onClick={() => setActiveTab('orders')}>
          <div className="nav-icon">üì¶</div>
          <div>Orders</div>
        </button>
        <button className="nav-item" onClick={logout}>
          <div className="nav-icon">üë§</div>
          <div>Profile</div>
        </button>
      </div>
    </div>
  );
}

export default CustomerDashboard;
