import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';

const LiveTrackingMap = ({ request, user }) => {
  const [driverLocation, setDriverLocation] = useState({
    latitude: 33.5731 + (Math.random() - 0.5) * 0.01,
    longitude: -7.5898 + (Math.random() - 0.5) * 0.01,
    address: 'Moving towards you...'
  });
  const [lastUpdate, setLastUpdate] = useState(new Date());
  const [socket, setSocket] = useState(null);
  const [estimatedTime, setEstimatedTime] = useState(Math.floor(Math.random() * 15) + 5);

  useEffect(() => {
    const newSocket = io('http://localhost:5000');
    setSocket(newSocket);
    newSocket.emit('join-room', request._id);

    newSocket.on('driver-location-update', (data) => {
      if (data.requestId === request._id) {
        setDriverLocation(data.location);
        setLastUpdate(new Date());
      }
    });

    // Simulate live tracking updates
    const interval = setInterval(() => {
      setDriverLocation(prev => ({
        latitude: prev.latitude + (Math.random() - 0.5) * 0.001,
        longitude: prev.longitude + (Math.random() - 0.5) * 0.001,
        address: getRandomAddress()
      }));
      setLastUpdate(new Date());
      setEstimatedTime(prev => Math.max(1, prev - Math.random() * 2));
    }, 3000);

    return () => {
      newSocket.disconnect();
      clearInterval(interval);
    };
  }, [request._id]);

  const getRandomAddress = () => {
    const addresses = [
      'Rue Mohammed V, Casablanca',
      'Boulevard Hassan II',
      'Avenue des FAR',
      'Rue Abderrahman Sahraoui',
      'Moving towards you...',
      'Near your location'
    ];
    return addresses[Math.floor(Math.random() * addresses.length)];
  };

  const getStatusMessage = (status) => {
    const messages = {
      pending: 'Looking for driver...',
      accepted: 'Driver heading to store',
      shopping: 'Driver shopping for your items',
      delivering: 'Driver coming to you',
      completed: 'Delivered!',
      cancelled: 'Cancelled'
    };
    return messages[status] || status;
  };

  return (
    <div style={{ margin: '15px 0' }}>
      {/* Live Tracking Display */}
      <div style={{
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        borderRadius: '15px',
        padding: '20px',
        color: 'white',
        position: 'relative',
        overflow: 'hidden'
      }}>
        {/* Animated background */}
        <div style={{
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: `
            radial-gradient(circle at 20% 20%, rgba(255,255,255,0.1) 0%, transparent 50%),
            radial-gradient(circle at 80% 80%, rgba(255,255,255,0.1) 0%, transparent 50%),
            radial-gradient(circle at 40% 60%, rgba(255,255,255,0.05) 0%, transparent 50%)
          `,
          animation: 'float 6s ease-in-out infinite'
        }} />

        <div style={{ position: 'relative', zIndex: 2 }}>
          <div style={{ textAlign: 'center', marginBottom: '20px' }}>
            <div style={{
              fontSize: '48px',
              marginBottom: '10px',
              animation: request.status === 'delivering' ? 'bounce 1s infinite' : 'none'
            }}>🚗</div>
            <h3 style={{ margin: 0, fontSize: '18px' }}>
              {getStatusMessage(request.status)}
            </h3>
            <div style={{ fontSize: '14px', opacity: 0.9, marginTop: '5px' }}>
              ETA: {Math.ceil(estimatedTime)} minutes
            </div>
          </div>

          {/* Driver Location Card */}
          <div style={{
            background: 'rgba(255,255,255,0.15)',
            borderRadius: '12px',
            padding: '15px',
            backdropFilter: 'blur(10px)',
            border: '1px solid rgba(255,255,255,0.2)'
          }}>
            <div style={{ display: 'flex', alignItems: 'center', marginBottom: '10px' }}>
              <div style={{
                width: '12px',
                height: '12px',
                background: '#4CAF50',
                borderRadius: '50%',
                marginRight: '8px',
                animation: 'pulse 2s infinite'
              }} />
              <span style={{ fontSize: '14px', fontWeight: 'bold' }}>Live Location</span>
            </div>

            <div style={{ fontSize: '16px', marginBottom: '8px' }}>
              📍 {driverLocation.address}
            </div>

            <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '12px', opacity: 0.8 }}>
              <span>Lat: {driverLocation.latitude.toFixed(4)}</span>
              <span>Lng: {driverLocation.longitude.toFixed(4)}</span>
              <span>Updated: {lastUpdate.toLocaleTimeString()}</span>
            </div>
          </div>

          {/* Route Info */}
          <div style={{
            display: 'grid',
            gridTemplateColumns: '1fr auto 1fr',
            alignItems: 'center',
            marginTop: '15px',
            gap: '10px'
          }}>
            <div style={{ textAlign: 'center' }}>
              <div style={{ fontSize: '20px', marginBottom: '5px' }}>🏪</div>
              <div style={{ fontSize: '12px', opacity: 0.9 }}>Pickup</div>
              <div style={{ fontSize: '11px', opacity: 0.7 }}>{request.store}</div>
            </div>

            <div style={{
              width: '40px',
              height: '2px',
              background: 'rgba(255,255,255,0.5)',
              position: 'relative'
            }}>
              <div style={{
                width: '8px',
                height: '8px',
                background: '#4CAF50',
                borderRadius: '50%',
                position: 'absolute',
                top: '-3px',
                left: '60%',
                animation: 'move 3s linear infinite'
              }} />
            </div>

            <div style={{ textAlign: 'center' }}>
              <div style={{ fontSize: '20px', marginBottom: '5px' }}>🏠</div>
              <div style={{ fontSize: '12px', opacity: 0.9 }}>Delivery</div>
              <div style={{ fontSize: '11px', opacity: 0.7 }}>Your Address</div>
            </div>
          </div>
        </div>
      </div>

      <style>
        {`
          @keyframes pulse {
            0%, 100% { opacity: 1; }
            50% { opacity: 0.5; }
          }
          @keyframes bounce {
            0%, 20%, 50%, 80%, 100% { transform: translateY(0); }
            40% { transform: translateY(-10px); }
            60% { transform: translateY(-5px); }
          }
          @keyframes float {
            0%, 100% { transform: translateY(0px) rotate(0deg); }
            33% { transform: translateY(-10px) rotate(1deg); }
            66% { transform: translateY(-5px) rotate(-1deg); }
          }
          @keyframes move {
            0% { left: 0%; }
            100% { left: 80%; }
          }
        `}
      </style>
    </div>
  );
};

export default LiveTrackingMap;
