import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import LiveTrackingMap from '../components/LiveTrackingMap';

const API_URL = 'http://localhost:5000/api';

function TrackDelivery({ user }) {
  const { requestId } = useParams();
  const [request, setRequest] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    loadRequest();
    // Set up polling for real-time updates
    const interval = setInterval(loadRequest, 5000);
    return () => clearInterval(interval);
  }, [requestId]);

  const loadRequest = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`${API_URL}/requests/my/${user._id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      const foundRequest = response.data.find(r => r._id === requestId);
      setRequest(foundRequest);
    } catch (error) {
      console.error('Error loading request:', error);
    }
    setLoading(false);
  };

  const getStatusColor = (status) => {
    const colors = {
      pending: '#FF9800',
      accepted: '#4CAF50',
      shopping: '#2196F3',
      delivering: '#9C27B0',
      completed: '#4CAF50',
      cancelled: '#F44336'
    };
    return colors[status] || '#666';
  };

  const getStatusMessage = (status) => {
    const messages = {
      pending: 'Looking for a driver...',
      accepted: 'Driver is on the way to the store',
      shopping: 'Driver is shopping for your items',
      delivering: 'Driver is on the way to you',
      completed: 'Delivery completed!',
      cancelled: 'Request was cancelled'
    };
    return messages[status] || status;
  };

  if (loading) {
    return (
      <div className="container">
        <div className="loading">Loading...</div>
      </div>
    );
  }

  if (!request) {
    return (
      <div className="container">
        <div className="card" style={{ textAlign: 'center', padding: '40px' }}>
          <p>Request not found</p>
          <button className="btn-primary" onClick={() => navigate('/')}>
            Go Back
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="app-header">
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <button 
            onClick={() => navigate('/')}
            style={{ background: 'none', border: 'none', color: 'white', fontSize: '24px', marginRight: '15px' }}
          >
            ‚Üê
          </button>
          <div>
            <div className="car-icon">üöó</div>
            <h1 className="app-title">Delivery</h1>
          </div>
        </div>
      </div>

      <LiveTrackingMap request={request} user={user} />

      {request.driverId && (
        <div className="card" style={{ marginTop: '15px' }}>
          <h3 style={{ margin: '0 0 15px 0', color: '#333' }}>Driver Contact</h3>
          <div style={{ display: 'flex', alignItems: 'center', marginBottom: '15px' }}>
            <div style={{
              width: '50px',
              height: '50px',
              background: '#4CAF50',
              borderRadius: '50%',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              color: 'white',
              fontSize: '20px',
              fontWeight: 'bold',
              marginRight: '15px'
            }}>
              {request.driverId.name.charAt(0)}
            </div>
            <div style={{ flex: 1 }}>
              <h4 style={{ margin: '0 0 5px 0' }}>{request.driverId.name}</h4>
              <div style={{ color: '#666', fontSize: '16px', marginBottom: '5px' }}>
                üìû {request.driverId.phone}
              </div>
              <div style={{ color: '#FFC107', fontSize: '14px' }}>
                ‚≠ê {request.driverId.rating?.toFixed(1) || '5.0'}
              </div>
            </div>
          </div>

          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '10px' }}>
            <button 
              onClick={() => window.open(`tel:${request.driverId.phone}`)}
              style={{
                background: '#2196F3',
                color: 'white',
                border: 'none',
                padding: '12px',
                borderRadius: '8px',
                fontSize: '14px',
                fontWeight: 'bold',
                cursor: 'pointer'
              }}
            >
              üìû Call
            </button>
            <button 
              onClick={() => window.open(`https://wa.me/${request.driverId.phone.replace(/[^0-9]/g, '')}?text=Hi, I'm tracking my delivery order`)}
              style={{
                background: '#25D366',
                color: 'white',
                border: 'none',
                padding: '12px',
                borderRadius: '8px',
                fontSize: '14px',
                fontWeight: 'bold',
                cursor: 'pointer'
              }}
            >
              üí¨ WhatsApp
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default TrackDelivery;
