import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import io from 'socket.io-client';

const API_URL = 'http://192.168.1.227:5000/api';

function CustomerDashboard({ user }) {
  const [requests, setRequests] = useState([]);
  const [availableDrivers, setAvailableDrivers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('home');
  const mapRef = useRef(null);
  const [map, setMap] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    loadMyRequests();
    loadAvailableDrivers();
    initMap();

    // Socket for real-time driver updates
    const socket = io('http://192.168.1.227:5000');
    socket.on('driver-locations', (drivers) => {
      setAvailableDrivers(drivers);
      updateDriversOnMap(drivers);
    });

    return () => socket.disconnect();
  }, []);

  const loadMyRequests = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`${API_URL}/requests/my/${user._id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setRequests(response.data);
    } catch (error) {
      console.error('Error loading requests:', error);
    }
    setLoading(false);
  };

  const loadAvailableDrivers = async () => {
    try {
      const response = await axios.get(`${API_URL}/drivers/available`);
      setAvailableDrivers(response.data);
    } catch (error) {
      console.error('Error loading drivers:', error);
    }
  };

  const initMap = () => {
    if (!window.google || !mapRef.current) return;

    const mapInstance = new window.google.maps.Map(mapRef.current, {
      zoom: 14,
      center: { lat: 33.5731, lng: -7.5898 },
      styles: [
        { featureType: "poi", stylers: [{ visibility: "off" }] },
        { featureType: "transit", stylers: [{ visibility: "off" }] }
      ],
      disableDefaultUI: true,
      zoomControl: false
    });

    setMap(mapInstance);
  };

  const updateDriversOnMap = (drivers) => {
    if (!map) return;

    drivers.forEach((driver, index) => {
      if (driver.currentLocation) {
        new window.google.maps.Marker({
          position: {
            lat: driver.currentLocation.latitude,
            lng: driver.currentLocation.longitude
          },
          map: map,
          icon: {
            path: 'M12 2C8.13 2 5 5.13 5 9c0 5.25 7 13 7 13s7-7.75 7-13c0-3.87-3.13-7-7-7z',
            fillColor: '#00D4AA',
            fillOpacity: 1,
            strokeColor: '#ffffff',
            strokeWeight: 2,
            scale: 1.2
          },
          animation: window.google.maps.Animation.DROP
        });
      }
    });
  };

  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    window.location.href = '/login';
  };

  return (
    <div className="app">
      {/* inDrive-style Header */}
      <div className="indrive-header">
        <div className="header-content">
          <div className="logo-section">
            <img src="/logo.png" alt="Delivery Connect" className="logo" />
            <div className="app-name">DeliveryConnect</div>
          </div>
          <div className="user-info">
            <div className="user-name">{user.name}</div>
            <div className="user-type">Customer</div>
          </div>
        </div>
      </div>

      {activeTab === 'home' && (
        <>
          {/* Map with Drivers */}
          <div className="map-container">
            <div className="map-overlay">
              <div className="location-info">
                <div className="location-icon">📍</div>
                <div className="location-text">Casablanca, Morocco</div>
              </div>
              <div className="drivers-count">{availableDrivers.length} drivers</div>
            </div>
            <div ref={mapRef} style={{ width: '100%', height: '100%' }} />
          </div>

          {/* Action Cards */}
          <div className="action-cards">
            <div className="action-grid">
              <button 
                className="action-card primary"
                onClick={() => navigate('/create-request')}
              >
                <span className="action-icon">🚀</span>
                <div className="action-title">New Delivery</div>
                <div className="action-subtitle">Get anything delivered</div>
              </button>
              <button 
                className="action-card"
                onClick={() => setActiveTab('drivers')}
              >
                <span className="action-icon">👥</span>
                <div className="action-title">View Drivers</div>
                <div className="action-subtitle">{availableDrivers.length} available</div>
              </button>
            </div>
          </div>

          {/* Recent Orders */}
          <div className="recent-section">
            <div className="section-header">
              <div className="section-title">Recent Orders</div>
              <button className="see-all" onClick={() => setActiveTab('orders')}>See all</button>
            </div>

            {loading ? (
              <div style={{ textAlign: 'center', padding: '20px' }}>Loading...</div>
            ) : requests.length === 0 ? (
              <div className="empty-state">
                <div className="empty-icon">📦</div>
                <div className="empty-title">No orders yet</div>
                <div className="empty-subtitle">Create your first delivery request!</div>
                <button className="primary-btn" onClick={() => navigate('/create-request')}>Start Delivery</button>
              </div>
            ) : (
              requests.slice(0, 3).map(request => (
                <div key={request._id} className="order-card">
                  <div className="order-header">
                    <div className="order-info">
                      <h4>{request.item}</h4>
                      <div className="order-store">from {request.store}</div>
                    </div>
                    <div className={`order-status status-${request.status}`}>
                      {request.status}
                    </div>
                  </div>
                  <div className="order-details">
                    <div className="order-price">{request.price} MAD</div>
                    {request.status !== 'completed' && (
                      <button 
                        className="track-btn"
                        onClick={() => navigate(`/track/${request._id}`)}
                      >
                        Track
                      </button>
                    )}
                  </div>
                </div>
              ))
            )}
          </div>
        </>
      )}

      {activeTab === 'drivers' && (
        <div style={{ padding: '20px' }}>
          <div className="section-header">
            <div className="section-title">Available Drivers</div>
            <button onClick={() => setActiveTab('home')} style={{ background: 'none', border: 'none', fontSize: '24px' }}>×</button>
          </div>
          {availableDrivers.map(driver => (
            <div key={driver._id} className="driver-card">
              <div className="driver-avatar">
                {driver.name?.charAt(0) || 'D'}
              </div>
              <div className="driver-info">
                <div className="driver-name">{driver.name || 'Unknown Driver'}</div>
                <div className="driver-rating">
                  <span>⭐</span>
                  <span>{driver.rating?.toFixed(1) || '5.0'}</span>
                  <span>({driver.totalReviews || 0})</span>
                </div>
                <div className="driver-vehicle">{driver.vehicle?.type} {driver.vehicle?.model}</div>
              </div>
              <div className="driver-status">
                <div className="status-online">🟢 Online</div>
                <div className="driver-distance">2 min away</div>
              </div>
            </div>
          ))}
        </div>
      )}

      {activeTab === 'orders' && (
        <div style={{ padding: '20px' }}>
          <div className="section-header">
            <div className="section-title">All Orders</div>
            <button onClick={() => setActiveTab('home')} style={{ background: 'none', border: 'none', fontSize: '24px' }}>×</button>
          </div>
          {requests.map(request => (
            <div key={request._id} className="order-card">
              <div className="order-header">
                <div className="order-info">
                  <h4>{request.item}</h4>
                  <div className="order-store">from {request.store}</div>
                </div>
                <div className={`order-status status-${request.status}`}>
                  {request.status}
                </div>
              </div>
              <div className="order-details">
                <div className="order-price">{request.price} MAD</div>
                {request.status !== 'completed' && (
                  <button 
                    className="track-btn"
                    onClick={() => navigate(`/track/${request._id}`)}
                  >
                    Track
                  </button>
                )}
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Bottom Navigation */}
      <div className="bottom-nav">
        <button 
          className={`nav-item ${activeTab === 'home' ? 'active' : ''}`}
          onClick={() => setActiveTab('home')}
        >
          <div className="nav-icon">🏠</div>
          <div className="nav-label">Home</div>
        </button>
        <button 
          className={`nav-item ${activeTab === 'drivers' ? 'active' : ''}`}
          onClick={() => setActiveTab('drivers')}
        >
          <div className="nav-icon">👥</div>
          <div className="nav-label">Drivers</div>
        </button>
        <button 
          className={`nav-item ${activeTab === 'orders' ? 'active' : ''}`}
          onClick={() => setActiveTab('orders')}
        >
          <div className="nav-icon">📦</div>
          <div className="nav-label">Orders</div>
        </button>
        <button 
          className="nav-item"
          onClick={logout}
        >
          <div className="nav-icon">👤</div>
          <div className="nav-label">Profile</div>
        </button>
      </div>

      <div style={{ height: '80px' }}></div>
    </div>
  );
}

export default CustomerDashboard;
