import axios from 'axios';

const API_URL = 'http://192.168.1.227:5000/api';

export class LocationTracker {
  constructor(userId) {
    this.userId = userId;
    this.watchId = null;
    this.isTracking = false;
  }

  async startTracking() {
    this.isTracking = true;

    if (!navigator.geolocation) {
      console.error('Geolocation not supported');
      this.useFallbackLocation();
      return;
    }

    try {
      // Check permission first
      const permission = await navigator.permissions.query({name: 'geolocation'});
      console.log('Location permission:', permission.state);

      if (permission.state === 'denied') {
        alert('Location access is blocked. Please enable location in your browser settings.');
        this.useFallbackLocation();
        return;
      }

      // Get current position with better error handling
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const liveLocation = {
            latitude: position.coords.latitude,
            longitude: position.coords.longitude
          };
          console.log('ðŸ“ Got live GPS location:', liveLocation);
          this.updateLocation(liveLocation.latitude, liveLocation.longitude);

          // Start live tracking with more lenient options
          this.watchId = navigator.geolocation.watchPosition(
            (pos) => {
              console.log('ðŸ“ Live GPS update:', pos.coords.latitude, pos.coords.longitude);
              this.updateLocation(pos.coords.latitude, pos.coords.longitude);
            },
            (error) => {
              console.error('GPS tracking error:', error);
              this.handleLocationError(error);
            },
            { 
              enableHighAccuracy: false, // Less strict for better compatibility
              timeout: 10000, // Longer timeout
              maximumAge: 30000 // Allow cached location up to 30 seconds
            }
          );
        },
        (error) => {
          console.error('Initial GPS error:', error);
          this.handleLocationError(error);
        },
        {
          enableHighAccuracy: false,
          timeout: 10000,
          maximumAge: 60000
        }
      );
    } catch (error) {
      console.error('Permission check failed:', error);
      this.useFallbackLocation();
    }
  }

  handleLocationError(error) {
    let errorMessage = '';
    switch(error.code) {
      case error.PERMISSION_DENIED:
        errorMessage = 'Location access denied. Please enable location permissions.';
        break;
      case error.POSITION_UNAVAILABLE:
        errorMessage = 'Location information unavailable.';
        break;
      case error.TIMEOUT:
        errorMessage = 'Location request timed out.';
        break;
      default:
        errorMessage = 'Unknown location error.';
        break;
    }

    console.error('Location error:', errorMessage);
    alert(errorMessage + ' Using approximate location instead.');
    this.useFallbackLocation();
  }

  useFallbackLocation() {
    const city = prompt('Enter your city (casablanca/marrakech/rabat):')?.toLowerCase() || 'casablanca';

    let mockLat, mockLng;
    if (city.includes('marrakech')) {
      mockLat = 31.6295 + (Math.random() - 0.5) * 0.01;
      mockLng = -7.9811 + (Math.random() - 0.5) * 0.01;
      console.log('Using mock Marrakech location');
    } else if (city.includes('rabat')) {
      mockLat = 34.0209 + (Math.random() - 0.5) * 0.01;
      mockLng = -6.8416 + (Math.random() - 0.5) * 0.01;
      console.log('Using mock Rabat location');
    } else {
      mockLat = 33.5731 + (Math.random() - 0.5) * 0.01;
      mockLng = -7.5898 + (Math.random() - 0.5) * 0.01;
      console.log('Using mock Casablanca location');
    }

    this.updateLocation(mockLat, mockLng);

    // Simulate movement
    this.watchId = setInterval(() => {
      const newLat = mockLat + (Math.random() - 0.5) * 0.001;
      const newLng = mockLng + (Math.random() - 0.5) * 0.001;
      this.updateLocation(newLat, newLng);
    }, 5000);
  }

  stopTracking() {
    if (this.watchId) {
      if (typeof this.watchId === 'number' && navigator.geolocation) {
        navigator.geolocation.clearWatch(this.watchId);
      } else {
        clearInterval(this.watchId);
      }
      this.watchId = null;
    }
    this.isTracking = false;
    console.log('ðŸš« Live location tracking stopped');
  }

  async updateLocation(latitude, longitude) {
    try {
      const token = localStorage.getItem('token');
      await axios.patch(`${API_URL}/users/${this.userId}/location`, {
        latitude,
        longitude,
        address: `${latitude.toFixed(4)}, ${longitude.toFixed(4)}`
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });

      // Cache location and broadcast to customers via socket
      if (window.driverSocket) {
        const locationData = {
          driverId: this.userId,
          location: { latitude, longitude },
          timestamp: Date.now()
        };

        window.driverSocket.emit('driver-location', locationData);
        window.driverSocket.emit('cache-driver-location', locationData);
      }
    } catch (error) {
      console.error('Failed to update location:', error);
    }
  }

  async getCurrentLocation() {
    return new Promise((resolve, reject) => {
      if (!navigator.geolocation) {
        console.log('Geolocation not supported, using stored location');
        this.getStoredLocation().then(resolve).catch(reject);
        return;
      }

      navigator.geolocation.getCurrentPosition(
        (position) => {
          const location = {
            latitude: position.coords.latitude,
            longitude: position.coords.longitude
          };
          console.log('ðŸ“ Got current GPS location:', location);
          resolve(location);
        },
        async (error) => {
          console.error('GPS getCurrentLocation error:', error);
          try {
            const storedLocation = await this.getStoredLocation();
            resolve(storedLocation);
          } catch (err) {
            reject(err);
          }
        },
        {
          enableHighAccuracy: false,
          timeout: 8000,
          maximumAge: 60000
        }
      );
    });
  }

  async getStoredLocation() {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`${API_URL}/users/${this.userId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      const userData = await response.json();

      if (userData.location?.latitude && userData.location?.longitude) {
        console.log('Using driver stored location:', userData.location);
        return {
          latitude: userData.location.latitude,
          longitude: userData.location.longitude
        };
      }
    } catch (error) {
      console.error('Error getting stored location:', error);
    }

    // Fallback to Casablanca
    console.log('Using default Casablanca location');
    return {
      latitude: 33.5731,
      longitude: -7.5898
    };
  }
}

export default LocationTracker;
