import axios from 'axios';

const API_URL = 'http://192.168.1.227:5000/api';

export class LocationTracker {
  constructor(userId) {
    this.userId = userId;
    this.watchId = null;
    this.isTracking = false;
  }

  startTracking() {
    this.isTracking = true;

    // Force GPS to work - try multiple times
    this.tryGetLocation(0);
  }

  tryGetLocation(attempt) {
    if (attempt > 5) {
      console.log('Using mock location after 5 attempts');
      this.useFallbackLocation();
      return;
    }

    if (!navigator.geolocation) {
      this.useFallbackLocation();
      return;
    }

    const options = {
      enableHighAccuracy: attempt < 2, // Try high accuracy first
      timeout: attempt < 2 ? 5000 : 15000,
      maximumAge: attempt < 2 ? 0 : 300000
    };

    navigator.geolocation.getCurrentPosition(
      (position) => {
        console.log('ðŸ“ GPS SUCCESS:', position.coords.latitude, position.coords.longitude);
        this.updateLocation(position.coords.latitude, position.coords.longitude);

        // Start watching with successful options
        this.watchId = navigator.geolocation.watchPosition(
          (pos) => {
            console.log('ðŸ“ Live update:', pos.coords.latitude, pos.coords.longitude);
            this.updateLocation(pos.coords.latitude, pos.coords.longitude);
          },
          () => {}, // Ignore watch errors
          options
        );
      },
      (error) => {
        console.log(`GPS attempt ${attempt + 1} failed:`, error.message);
        setTimeout(() => this.tryGetLocation(attempt + 1), 1000);
      },
      options
    );
  }



  useFallbackLocation() {
    // Default to Casablanca
    const mockLat = 33.5731 + (Math.random() - 0.5) * 0.01;
    const mockLng = -7.5898 + (Math.random() - 0.5) * 0.01;
    console.log('Using default location (Casablanca)');

    this.updateLocation(mockLat, mockLng);

    // Simulate movement
    this.watchId = setInterval(() => {
      const newLat = mockLat + (Math.random() - 0.5) * 0.001;
      const newLng = mockLng + (Math.random() - 0.5) * 0.001;
      this.updateLocation(newLat, newLng);
    }, 5000);
  }

  stopTracking() {
    if (this.watchId) {
      if (typeof this.watchId === 'number' && navigator.geolocation) {
        navigator.geolocation.clearWatch(this.watchId);
      } else {
        clearInterval(this.watchId);
      }
      this.watchId = null;
    }
    this.isTracking = false;
    console.log('ðŸš« Live location tracking stopped');
  }

  async updateLocation(latitude, longitude) {
    try {
      const token = localStorage.getItem('token');
      await axios.patch(`${API_URL}/users/${this.userId}/location`, {
        latitude,
        longitude,
        address: `${latitude.toFixed(4)}, ${longitude.toFixed(4)}`
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });

      // Cache location and broadcast to customers via socket
      if (window.driverSocket) {
        const locationData = {
          driverId: this.userId,
          location: { latitude, longitude },
          timestamp: Date.now()
        };

        window.driverSocket.emit('driver-location', locationData);
        window.driverSocket.emit('cache-driver-location', locationData);
      }
    } catch (error) {
      console.error('Failed to update location:', error);
    }
  }

  async getCurrentLocation() {
    return new Promise((resolve) => {
      if (!navigator.geolocation) {
        resolve({ latitude: 33.5731, longitude: -7.5898 });
        return;
      }

      // Try high accuracy first
      navigator.geolocation.getCurrentPosition(
        (position) => {
          resolve({
            latitude: position.coords.latitude,
            longitude: position.coords.longitude
          });
        },
        () => {
          // Try low accuracy
          navigator.geolocation.getCurrentPosition(
            (position) => {
              resolve({
                latitude: position.coords.latitude,
                longitude: position.coords.longitude
              });
            },
            () => resolve({ latitude: 33.5731, longitude: -7.5898 }),
            { enableHighAccuracy: false, timeout: 15000, maximumAge: 600000 }
          );
        },
        { enableHighAccuracy: true, timeout: 5000, maximumAge: 0 }
      );
    });
  }


}

export default LocationTracker;
