import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import LocationSelector from '../components/LocationSelector';

const API_URL = 'http://localhost:5000/api';

function Profile({ user, setUser }) {
  const [homeAddress, setHomeAddress] = useState(user?.homeAddress || null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleLocationSelect = (type, location) => {
    setHomeAddress(location);
  };

  const saveHomeAddress = async () => {
    if (!homeAddress) {
      alert('Please select your home address on the map');
      return;
    }

    setLoading(true);
    try {
      const response = await axios.put(`${API_URL}/users/${user._id}`, {
        homeAddress
      });

      setUser(response.data.user);
      alert('Home address saved successfully!');
      navigate('/');
    } catch (error) {
      alert('Error saving address');
    }
    setLoading(false);
  };

  return (
    <div className="container">
      <div className="app-header">
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <button 
            onClick={() => navigate('/')}
            style={{ background: 'none', border: 'none', color: 'white', fontSize: '24px', marginRight: '15px' }}
          >
            ‚Üê
          </button>
          <div>
            <div className="car-icon">üë§</div>
            <h1 className="app-title">Profile</h1>
          </div>
        </div>
      </div>

      <div className="card">
        <h3>Set Your Home Address</h3>
        <p style={{ color: '#666', marginBottom: '20px' }}>
          This will be used as your default pickup location for deliveries
        </p>

        <LocationSelector
          onLocationSelect={handleLocationSelect}
          pickupLocation={homeAddress}
          mode="home"
        />

        {homeAddress && (
          <div style={{ margin: '15px 0', padding: '10px', background: '#f0f8f0', borderRadius: '8px' }}>
            <strong>Selected Address:</strong><br />
            {homeAddress.address}
          </div>
        )}

        <button 
          onClick={saveHomeAddress}
          className="btn-secondary"
          disabled={loading || !homeAddress}
          style={{ marginTop: '20px' }}
        >
          {loading ? 'Saving...' : 'Save Home Address'}
        </button>
      </div>
    </div>
  );
}

export default Profile;
