import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const API_URL = 'http://localhost:5000/api';

function TrackDelivery({ user }) {
  const { requestId } = useParams();
  const [request, setRequest] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    loadRequest();
    // Set up polling for real-time updates
    const interval = setInterval(loadRequest, 5000);
    return () => clearInterval(interval);
  }, [requestId]);

  const loadRequest = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`${API_URL}/requests/my/${user._id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      const foundRequest = response.data.find(r => r._id === requestId);
      setRequest(foundRequest);
    } catch (error) {
      console.error('Error loading request:', error);
    }
    setLoading(false);
  };

  const getStatusColor = (status) => {
    const colors = {
      pending: '#FF9800',
      accepted: '#4CAF50',
      shopping: '#2196F3',
      delivering: '#9C27B0',
      completed: '#4CAF50',
      cancelled: '#F44336'
    };
    return colors[status] || '#666';
  };

  const getStatusMessage = (status) => {
    const messages = {
      pending: 'Looking for a driver...',
      accepted: 'Driver is on the way to the store',
      shopping: 'Driver is shopping for your items',
      delivering: 'Driver is on the way to you',
      completed: 'Delivery completed!',
      cancelled: 'Request was cancelled'
    };
    return messages[status] || status;
  };

  if (loading) {
    return (
      <div className="container">
        <div className="loading">Loading...</div>
      </div>
    );
  }

  if (!request) {
    return (
      <div className="container">
        <div className="card" style={{ textAlign: 'center', padding: '40px' }}>
          <p>Request not found</p>
          <button className="btn-primary" onClick={() => navigate('/')}>
            Go Back
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="app-header">
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <button 
            onClick={() => navigate('/')}
            style={{ background: 'none', border: 'none', color: 'white', fontSize: '24px', marginRight: '15px' }}
          >
            ‚Üê
          </button>
          <div>
            <div className="car-icon">üöó</div>
            <h1 className="app-title">Delivery</h1>
          </div>
        </div>
      </div>

      <div className="map-placeholder">
        üìç Live Tracking
        <br />
        <small>Driver location updates</small>
      </div>

      <div style={{ display: 'flex', justifyContent: 'space-between', margin: '15px 0' }}>
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <div style={{ width: '12px', height: '12px', background: '#4CAF50', borderRadius: '50%', marginRight: '10px' }}></div>
          <span>{request.pickupLocation.address}</span>
        </div>
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <div style={{ width: '12px', height: '12px', background: '#333', borderRadius: '50%', marginRight: '10px' }}></div>
          <span>{request.deliveryLocation.address}</span>
        </div>
      </div>

      <div className="card">
        <div style={{ textAlign: 'center', marginBottom: '20px' }}>
          <div style={{ 
            fontSize: '24px', 
            fontWeight: 'bold', 
            color: getStatusColor(request.status),
            marginBottom: '10px'
          }}>
            {getStatusMessage(request.status)}
          </div>
          <div style={{ fontSize: '16px', color: '#666' }}>
            {request.item} from {request.store}
          </div>
        </div>

        {request.driverId && (
          <div style={{ 
            background: '#f5f5f5', 
            padding: '15px', 
            borderRadius: '10px',
            marginBottom: '20px'
          }}>
            <div style={{ display: 'flex', alignItems: 'center' }}>
              <div className="driver-avatar" style={{ marginRight: '15px' }}>
                {request.driverId.name.charAt(0)}
              </div>
              <div style={{ flex: 1 }}>
                <h4 style={{ margin: 0 }}>{request.driverId.name}</h4>
                <div style={{ color: '#FFC107', fontSize: '14px' }}>
                  ‚≠ê {request.driverId.rating?.toFixed(1) || '5.0'}
                </div>
                {request.driverId.vehicle && (
                  <div style={{ color: '#666', fontSize: '14px' }}>
                    {request.driverId.vehicle.type} {request.driverId.vehicle.model}
                  </div>
                )}
              </div>
              <div style={{ textAlign: 'right' }}>
                <div style={{ fontSize: '20px', fontWeight: 'bold', color: '#4CAF50' }}>
                  {request.price} MAD
                </div>
                <div style={{ fontSize: '12px', color: '#666' }}>
                  {request.status === 'delivering' ? 'Arriving' : 'Fee'}
                </div>
              </div>
            </div>
          </div>
        )}

        <div className="request-details">
          <p><strong>Order Details:</strong></p>
          <p style={{ marginLeft: '15px', color: '#666' }}>‚Ä¢ {request.item}</p>
          {request.notes && (
            <p style={{ marginLeft: '15px', color: '#666' }}>‚Ä¢ {request.notes}</p>
          )}
        </div>

        {request.status === 'delivering' && (
          <button className="btn-secondary" style={{ marginTop: '20px' }}>
            Call Driver
          </button>
        )}

        {request.status === 'completed' && (
          <div style={{ textAlign: 'center', marginTop: '20px' }}>
            <p style={{ color: '#4CAF50', fontWeight: 'bold', marginBottom: '15px' }}>
              ‚úÖ Delivery Completed!
            </p>
            <button className="btn-primary" onClick={() => navigate('/')}>
              Back to Home
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

export default TrackDelivery;
