import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const API_URL = 'http://localhost:5000/api';

function DriverDashboard({ user }) {
  const [pendingRequests, setPendingRequests] = useState([]);
  const [myRequests, setMyRequests] = useState([]);
  const [isOnline, setIsOnline] = useState(user.isOnline || false);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('available');
  const navigate = useNavigate();

  useEffect(() => {
    loadPendingRequests();
    loadMyRequests();
  }, []);

  const loadPendingRequests = async () => {
    try {
      const response = await axios.get(`${API_URL}/requests/pending`);
      setPendingRequests(response.data);
    } catch (error) {
      console.error('Error loading requests:', error);
    }
    setLoading(false);
  };

  const loadMyRequests = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`${API_URL}/requests/my/${user._id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMyRequests(response.data.filter(r => r.driverId?._id === user._id));
    } catch (error) {
      console.error('Error loading my requests:', error);
    }
  };

  const toggleOnlineStatus = async () => {
    try {
      const newStatus = !isOnline;
      await axios.patch(`${API_URL}/users/${user._id}/online`, {
        isOnline: newStatus
      });
      setIsOnline(newStatus);
    } catch (error) {
      console.error('Error updating status:', error);
    }
  };

  const acceptRequest = async (requestId) => {
    try {
      await axios.post(`${API_URL}/requests/${requestId}/accept`, {
        driverId: user._id
      });

      // Remove from pending and reload
      setPendingRequests(prev => prev.filter(r => r._id !== requestId));
      loadMyRequests();
      alert('Request accepted! Contact the customer to coordinate.');
    } catch (error) {
      alert('Error accepting request. It may have been taken by another driver.');
      loadPendingRequests();
    }
  };

  const updateRequestStatus = async (requestId, status) => {
    try {
      await axios.patch(`${API_URL}/requests/${requestId}/status`, { status });
      loadMyRequests();
    } catch (error) {
      console.error('Error updating status:', error);
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    window.location.href = '/login';
  };

  const getStatusColor = (status) => {
    const colors = {
      pending: '#FF9800',
      accepted: '#4CAF50',
      shopping: '#2196F3',
      delivering: '#9C27B0',
      completed: '#4CAF50',
      cancelled: '#F44336'
    };
    return colors[status] || '#666';
  };

  return (
    <div className="container">
      <div className="app-header">
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <div>
            <div className="car-icon">🚗</div>
            <h1 className="app-title">Delivery</h1>
          </div>
          <div style={{ textAlign: 'right' }}>
            <div style={{ fontSize: '14px', opacity: 0.9 }}>Driver</div>
            <div style={{ fontSize: '18px', fontWeight: 'bold' }}>{user.name}</div>
            <div style={{ fontSize: '12px', opacity: 0.8 }}>
              ⭐ {user.rating?.toFixed(1) || '5.0'}
            </div>
          </div>
        </div>
      </div>

      <div className="card">
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <div>
            <h3 style={{ margin: 0 }}>Status: {isOnline ? 'Online' : 'Offline'}</h3>
            <p style={{ margin: '5px 0', color: '#666', fontSize: '14px' }}>
              {isOnline ? 'You can receive requests' : 'Go online to receive requests'}
            </p>
          </div>
          <button 
            onClick={toggleOnlineStatus}
            style={{
              background: isOnline ? '#4CAF50' : '#666',
              color: 'white',
              border: 'none',
              padding: '10px 20px',
              borderRadius: '20px',
              cursor: 'pointer'
            }}
          >
            {isOnline ? 'Go Offline' : 'Go Online'}
          </button>
        </div>
      </div>

      <div style={{ display: 'flex', margin: '20px 0', borderBottom: '1px solid #e0e0e0' }}>
        <button 
          onClick={() => setActiveTab('available')}
          style={{
            flex: 1,
            padding: '15px',
            border: 'none',
            background: 'none',
            borderBottom: activeTab === 'available' ? '3px solid #4CAF50' : 'none',
            color: activeTab === 'available' ? '#4CAF50' : '#666',
            fontWeight: activeTab === 'available' ? 'bold' : 'normal'
          }}
        >
          Available ({pendingRequests.length})
        </button>
        <button 
          onClick={() => setActiveTab('my-deliveries')}
          style={{
            flex: 1,
            padding: '15px',
            border: 'none',
            background: 'none',
            borderBottom: activeTab === 'my-deliveries' ? '3px solid #4CAF50' : 'none',
            color: activeTab === 'my-deliveries' ? '#4CAF50' : '#666',
            fontWeight: activeTab === 'my-deliveries' ? 'bold' : 'normal'
          }}
        >
          My Deliveries ({myRequests.length})
        </button>
      </div>

      <div style={{ display: 'flex', justifyContent: 'flex-end', margin: '10px 0' }}>
        <button onClick={logout} style={{ background: 'none', border: 'none', color: '#666' }}>
          Logout
        </button>
      </div>

      {activeTab === 'available' ? (
        loading ? (
          <div className="loading">Loading requests...</div>
        ) : pendingRequests.length === 0 ? (
          <div className="card" style={{ textAlign: 'center', padding: '40px' }}>
            <p style={{ color: '#666', fontSize: '16px' }}>No requests available</p>
            <p style={{ color: '#999', fontSize: '14px' }}>Check back later for new delivery requests</p>
          </div>
        ) : (
          pendingRequests.map(request => (
            <div key={request._id} className="request-card">
              <div className="request-header">
                <div>
                  <h4 style={{ margin: 0, color: '#333' }}>{request.item}</h4>
                  <p style={{ margin: '5px 0', color: '#666', fontSize: '14px' }}>
                    from {request.store}
                  </p>
                </div>
                <div className="request-price">{request.price} MAD</div>
              </div>

              <div className="request-details">
                <p><strong>Customer:</strong> {request.customerId.name} 
                  <span style={{ color: '#FFC107', marginLeft: '5px' }}>
                    ⭐ {request.customerId.rating?.toFixed(1) || '5.0'}
                  </span>
                </p>
                <p><strong>Pickup:</strong> {request.pickupLocation.address}</p>
                <p><strong>Delivery:</strong> {request.deliveryLocation.address}</p>
                {request.notes && (
                  <p><strong>Notes:</strong> {request.notes}</p>
                )}
              </div>

              <button 
                className="btn-primary"
                onClick={() => acceptRequest(request._id)}
                style={{ marginTop: '15px' }}
              >
                Accept Request
              </button>
            </div>
          ))
        )
      ) : (
        myRequests.length === 0 ? (
          <div className="card" style={{ textAlign: 'center', padding: '40px' }}>
            <p style={{ color: '#666', fontSize: '16px' }}>No active deliveries</p>
            <p style={{ color: '#999', fontSize: '14px' }}>Accept requests to start earning</p>
          </div>
        ) : (
          myRequests.map(request => (
            <div key={request._id} className="request-card">
              <div className="request-header">
                <div>
                  <h4 style={{ margin: 0, color: '#333' }}>{request.item}</h4>
                  <p style={{ margin: '5px 0', color: '#666', fontSize: '14px' }}>
                    from {request.store}
                  </p>
                </div>
                <div className="request-price">{request.price} MAD</div>
              </div>

              <div className="request-details">
                <p><strong>Status:</strong> 
                  <span style={{ color: getStatusColor(request.status), marginLeft: '5px' }}>
                    {request.status.charAt(0).toUpperCase() + request.status.slice(1)}
                  </span>
                </p>
                <p><strong>Customer:</strong> {request.customerId.name}</p>
                <p><strong>Phone:</strong> {request.customerId.phone}</p>
                <p><strong>Pickup:</strong> {request.pickupLocation.address}</p>
                <p><strong>Delivery:</strong> {request.deliveryLocation.address}</p>
              </div>

              {request.status === 'accepted' && (
                <button 
                  className="btn-primary"
                  onClick={() => updateRequestStatus(request._id, 'shopping')}
                  style={{ marginTop: '15px' }}
                >
                  Start Shopping
                </button>
              )}

              {request.status === 'shopping' && (
                <button 
                  className="btn-primary"
                  onClick={() => updateRequestStatus(request._id, 'delivering')}
                  style={{ marginTop: '15px' }}
                >
                  Start Delivery
                </button>
              )}

              {request.status === 'delivering' && (
                <button 
                  className="btn-secondary"
                  onClick={() => updateRequestStatus(request._id, 'completed')}
                  style={{ marginTop: '15px' }}
                >
                  Mark as Delivered
                </button>
              )}
            </div>
          ))
        )
      )}
    </div>
  );
}

export default DriverDashboard;
