import React, { useEffect, useState, useRef } from 'react';
import io from 'socket.io-client';

const LiveTrackingMap = ({ request, user }) => {
  const [driverLocation, setDriverLocation] = useState(null);
  const [lastUpdate, setLastUpdate] = useState(null);
  const [socket, setSocket] = useState(null);
  const [estimatedTime, setEstimatedTime] = useState(15);
  const mapRef = useRef(null);
  const [map, setMap] = useState(null);
  const [driverMarker, setDriverMarker] = useState(null);

  useEffect(() => {
    const newSocket = io('http://localhost:5000');
    setSocket(newSocket);
    newSocket.emit('join-room', request._id);

    newSocket.on('driver-location-update', (data) => {
      if (data.requestId === request._id) {
        setDriverLocation(data.location);
        setLastUpdate(new Date(data.timestamp));
        updateDriverMarker(data.location);
        if (data.location.latitude && data.location.longitude) {
          const distance = calculateDistance(
            data.location.latitude, data.location.longitude,
            request.deliveryLocation.latitude, request.deliveryLocation.longitude
          );
          const eta = Math.max(2, Math.ceil(distance * 2));
          setEstimatedTime(eta);
        }
      }
    });

    return () => newSocket.disconnect();
  }, [request._id]);

  useEffect(() => {
    const timer = setTimeout(() => {
      if (window.google && window.google.maps && mapRef.current) {
        const mapInstance = new window.google.maps.Map(mapRef.current, {
          zoom: 16,
          center: {
            lat: request.deliveryLocation.latitude || 31.6301,
            lng: request.deliveryLocation.longitude || -8.0047
          },
          disableDefaultUI: true,
          zoomControl: false,
          streetViewControl: false,
          fullscreenControl: false,
          mapTypeControl: false,
          styles: [
            {
              featureType: 'poi',
              stylers: [{ visibility: 'off' }]
            },
            {
              featureType: 'transit',
              stylers: [{ visibility: 'off' }]
            }
          ]
        });

        setMap(mapInstance);
      }
    }, 2000);

    return () => clearTimeout(timer);
  }, []);

  const updateDriverMarker = (location) => {
    if (!map || !location || !location.latitude || !location.longitude) return;

    console.log('🚗 Driver at:', location.latitude, location.longitude);

    if (driverMarker) {
      // Smooth animation to new position
      const newPos = {lat: location.latitude, lng: location.longitude};
      driverMarker.setPosition(newPos);
      map.panTo(newPos);
    } else {
      // Create simple car marker
      const carIcon = {
        path: 'M17.402,0H5.643C2.526,0,0,3.467,0,6.584v34.804c0,3.116,2.526,5.644,5.643,5.644h11.759c3.116,0,5.644-2.527,5.644-5.644V6.584C23.044,3.467,20.518,0,17.402,0z M22.057,14.188v11.665l-2.729,0.351v4.329L22.057,14.188z M20.625,10.773c-1.016,3.9-2.219,8.51-2.219,8.51H4.638s-1.203-4.61-2.219-8.51C1.219,7.773,2.522,4.638,5.643,4.638h11.759C20.522,4.638,21.825,7.773,20.625,10.773z M22.057,24.188L19.328,40.532v4.329l2.729,0.351V24.188z',
        fillColor: '#FF5722',
        fillOpacity: 1,
        strokeColor: '#FFFFFF',
        strokeWeight: 2,
        scale: 0.8,
        anchor: new window.google.maps.Point(11, 40)
      };

      const marker = new window.google.maps.Marker({
        position: {lat: location.latitude, lng: location.longitude},
        map: map,
        icon: carIcon,
        title: 'Driver',
        zIndex: 1000
      });

      setDriverMarker(marker);
      map.setCenter({lat: location.latitude, lng: location.longitude});
      map.setZoom(17);
    }
  };

  const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371;
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a = Math.sin(dLat/2) * Math.sin(dLat/2) +
      Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
      Math.sin(dLon/2) * Math.sin(dLon/2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
    return R * c;
  };

  const getStatusMessage = (status) => {
    const messages = {
      pending: 'Looking for driver...',
      accepted: 'Driver heading to store',
      shopping: 'Driver shopping for your items',
      delivering: 'Driver coming to you',
      completed: 'Delivered!',
      cancelled: 'Cancelled'
    };
    return messages[status] || status;
  };

  return (
    <div style={{ margin: '15px 0' }}>
      <div style={{ position: 'relative' }}>
        <div 
          ref={mapRef}
          style={{
            width: '100%',
            height: '350px',
            borderRadius: '15px',
            border: '2px solid #e0e0e0',
            overflow: 'hidden'
          }}
        />

        {/* Simple Status Bar */}
        <div style={{
          position: 'absolute',
          top: '20px',
          left: '20px',
          right: '20px',
          background: 'rgba(0,0,0,0.8)',
          color: 'white',
          padding: '15px',
          borderRadius: '10px',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          zIndex: 1000
        }}>
          <div>
            <div style={{ fontSize: '16px', fontWeight: 'bold' }}>
              🚗 {getStatusMessage(request.status)}
            </div>
            {driverLocation && (
              <div style={{ fontSize: '12px', opacity: 0.8, marginTop: '3px' }}>
                Updated: {lastUpdate?.toLocaleTimeString()}
              </div>
            )}
          </div>
          <div style={{ textAlign: 'right' }}>
            <div style={{ fontSize: '18px', fontWeight: 'bold', color: '#4CAF50' }}>
              {Math.ceil(estimatedTime)} min
            </div>
            <div style={{ fontSize: '12px', opacity: 0.8 }}>
              ETA
            </div>
          </div>
        </div>

        {/* Driver Info */}
        {driverLocation && (
          <div style={{
            position: 'absolute',
            bottom: '20px',
            left: '20px',
            right: '20px',
            background: 'rgba(255,87,34,0.95)',
            color: 'white',
            padding: '12px',
            borderRadius: '10px',
            textAlign: 'center',
            zIndex: 1000
          }}>
            <div style={{ fontSize: '14px', fontWeight: 'bold' }}>
              📍 Driver Live Location
            </div>
            <div style={{ fontSize: '12px', opacity: 0.9, marginTop: '3px' }}>
              Moving towards you...
            </div>
          </div>
        )}

        {!driverLocation && (
          <div style={{
            position: 'absolute',
            bottom: '20px',
            left: '20px',
            right: '20px',
            background: 'rgba(0,0,0,0.7)',
            color: 'white',
            padding: '12px',
            borderRadius: '10px',
            textAlign: 'center',
            zIndex: 1000
          }}>
            📍 Waiting for driver location...
          </div>
        )}
      </div>
    </div>
  );
};

export default LiveTrackingMap;
