import axios from 'axios';

const API_URL = 'http://192.168.1.227:5000/api';

export class LocationTracker {
  constructor(userId) {
    this.userId = userId;
    this.watchId = null;
    this.isTracking = false;
  }

  startTracking() {
    this.isTracking = true;

    // Get live GPS location immediately
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const liveLocation = {
            latitude: position.coords.latitude,
            longitude: position.coords.longitude
          };
          console.log('ðŸ“ Got live GPS location:', liveLocation);
          this.updateLocation(liveLocation.latitude, liveLocation.longitude);

          // Start live tracking
          this.watchId = navigator.geolocation.watchPosition(
            (pos) => {
              console.log('ðŸ“ Live GPS update:', pos.coords.latitude, pos.coords.longitude);
              this.updateLocation(pos.coords.latitude, pos.coords.longitude);
            },
            (error) => console.error('GPS error:', error),
            { enableHighAccuracy: true, timeout: 5000, maximumAge: 0 }
          );
        },
        (error) => {
          console.log('GPS failed, using mock location near Casablanca');
          const mockLat = 33.5731 + (Math.random() - 0.5) * 0.01;
          const mockLng = -7.5898 + (Math.random() - 0.5) * 0.01;
          this.updateLocation(mockLat, mockLng);

          // Simulate movement
          this.watchId = setInterval(() => {
            const newLat = mockLat + (Math.random() - 0.5) * 0.001;
            const newLng = mockLng + (Math.random() - 0.5) * 0.001;
            this.updateLocation(newLat, newLng);
          }, 5000);
        }
      );
    }
  }

  stopTracking() {
    if (this.watchId) {
      if (navigator.geolocation) {
        navigator.geolocation.clearWatch(this.watchId);
      } else {
        clearInterval(this.watchId);
      }
      this.watchId = null;
    }
    this.isTracking = false;
    console.log('ðŸš« Live location tracking stopped');
  }

  async updateLocation(latitude, longitude) {
    try {
      const token = localStorage.getItem('token');
      await axios.patch(`${API_URL}/users/${this.userId}/location`, {
        latitude,
        longitude,
        address: `${latitude.toFixed(4)}, ${longitude.toFixed(4)}`
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });

      // Cache location and broadcast to customers via socket
      if (window.driverSocket) {
        const locationData = {
          driverId: this.userId,
          location: { latitude, longitude },
          timestamp: Date.now()
        };

        window.driverSocket.emit('driver-location', locationData);
        window.driverSocket.emit('cache-driver-location', locationData);
      }
    } catch (error) {
      console.error('Failed to update location:', error);
    }
  }

  async getCurrentLocation() {
    // Get driver's stored location from database
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`${API_URL}/users/${this.userId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      const userData = await response.json();

      if (userData.location?.latitude && userData.location?.longitude) {
        console.log('Using driver stored location:', userData.location);
        return {
          latitude: userData.location.latitude,
          longitude: userData.location.longitude
        };
      }
    } catch (error) {
      console.error('Error getting stored location:', error);
    }

    // Fallback to Casablanca
    console.log('Using default Casablanca location');
    return {
      latitude: 33.5731,
      longitude: -7.5898
    };
  }
}

export default LocationTracker;
