import React, { useEffect, useState, useRef } from 'react';
import io from 'socket.io-client';

const LiveTrackingMap = ({ request, user }) => {
  const [driverLocation, setDriverLocation] = useState(null);
  const [lastUpdate, setLastUpdate] = useState(null);
  const [socket, setSocket] = useState(null);
  const [estimatedTime, setEstimatedTime] = useState(15);
  const mapRef = useRef(null);
  const [map, setMap] = useState(null);
  const [driverMarker, setDriverMarker] = useState(null);

  useEffect(() => {
    const newSocket = io('http://localhost:5000');
    setSocket(newSocket);
    newSocket.emit('join-room', request._id);

    newSocket.on('driver-location-update', (data) => {
      if (data.requestId === request._id) {
        setDriverLocation(data.location);
        setLastUpdate(new Date(data.timestamp));
        updateDriverMarker(data.location);
        if (data.location.latitude && data.location.longitude) {
          const distance = calculateDistance(
            data.location.latitude, data.location.longitude,
            request.deliveryLocation.latitude, request.deliveryLocation.longitude
          );
          // Fix ETA: distance in km, reasonable speed 30km/h = 2min per km
          const eta = Math.max(2, Math.ceil(distance * 2));
          console.log(`Distance: ${distance.toFixed(2)}km, ETA: ${eta}min`);
          setEstimatedTime(eta);
        }
      }
    });

    return () => newSocket.disconnect();
  }, [request._id]);

  useEffect(() => {
    // Wait for Google Maps to load
    const checkGoogleMaps = () => {
      if (window.google && window.google.maps) {
        initializeMap();
      } else {
        setTimeout(checkGoogleMaps, 500);
      }
    };
    checkGoogleMaps();
  }, []);

  const initializeMap = () => {
    if (!window.google || !mapRef.current) return;

    console.log('Creating Google Map...');

    const mapInstance = new window.google.maps.Map(mapRef.current, {
      zoom: 15,
      center: new window.google.maps.LatLng(
        request.deliveryLocation.latitude || 33.5731, 
        request.deliveryLocation.longitude || -7.5898
      ),
      mapTypeId: window.google.maps.MapTypeId.ROADMAP,
      disableDefaultUI: false,
      zoomControl: true,
      streetViewControl: false
    });

    setMap(mapInstance);

    // Add pickup marker
    new window.google.maps.Marker({
      position: new window.google.maps.LatLng(
        request.pickupLocation.latitude, 
        request.pickupLocation.longitude
      ),
      map: mapInstance,
      title: 'Pickup: ' + request.store,
      icon: {
        url: 'https://maps.google.com/mapfiles/ms/icons/green-dot.png'
      }
    });

    // Add delivery marker
    new window.google.maps.Marker({
      position: new window.google.maps.LatLng(
        request.deliveryLocation.latitude, 
        request.deliveryLocation.longitude
      ),
      map: mapInstance,
      title: 'Delivery Address',
      icon: {
        url: 'https://maps.google.com/mapfiles/ms/icons/red-dot.png'
      }
    });

    // Fit bounds to show both markers
    const bounds = new window.google.maps.LatLngBounds();
    bounds.extend(new window.google.maps.LatLng(request.pickupLocation.latitude, request.pickupLocation.longitude));
    bounds.extend(new window.google.maps.LatLng(request.deliveryLocation.latitude, request.deliveryLocation.longitude));
    mapInstance.fitBounds(bounds);

    console.log('Map initialized successfully');
  };

  const updateDriverMarker = (location) => {
    if (!map || !location || !location.latitude || !location.longitude) return;

    if (driverMarker) {
      driverMarker.setPosition(new window.google.maps.LatLng(location.latitude, location.longitude));
    } else {
      const marker = new window.google.maps.Marker({
        position: new window.google.maps.LatLng(location.latitude, location.longitude),
        map: map,
        title: 'Driver Live Location',
        icon: {
          url: 'https://maps.google.com/mapfiles/ms/icons/blue-dot.png'
        },
        zIndex: 1000
      });
      setDriverMarker(marker);
    }

    map.panTo(new window.google.maps.LatLng(location.latitude, location.longitude));
  };

  const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371;
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a = Math.sin(dLat/2) * Math.sin(dLat/2) +
      Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
      Math.sin(dLon/2) * Math.sin(dLon/2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
    return R * c;
  };

  const getStatusMessage = (status) => {
    const messages = {
      pending: 'Looking for driver...',
      accepted: 'Driver heading to store',
      shopping: 'Driver shopping for your items',
      delivering: 'Driver coming to you',
      completed: 'Delivered!',
      cancelled: 'Cancelled'
    };
    return messages[status] || status;
  };

  return (
    <div style={{ margin: '15px 0' }}>
      <div style={{ position: 'relative' }}>
        <div 
          ref={mapRef}
          style={{
            width: '100%',
            height: '300px',
            borderRadius: '15px',
            border: '2px solid #e0e0e0',
            overflow: 'hidden',
            backgroundColor: '#f0f0f0'
          }}
        />

        <div style={{
          position: 'absolute',
          top: '15px',
          left: '15px',
          background: 'rgba(255,255,255,0.95)',
          padding: '8px 12px',
          borderRadius: '20px',
          fontSize: '12px',
          fontWeight: 'bold',
          color: '#333',
          boxShadow: '0 2px 10px rgba(0,0,0,0.2)',
          zIndex: 1000
        }}>
          üöó {getStatusMessage(request.status)}
        </div>

        <div style={{
          position: 'absolute',
          top: '15px',
          right: '15px',
          background: '#4CAF50',
          color: 'white',
          padding: '8px 12px',
          borderRadius: '20px',
          fontSize: '12px',
          fontWeight: 'bold',
          boxShadow: '0 2px 10px rgba(76,175,80,0.3)',
          zIndex: 1000
        }}>
          ETA: {Math.ceil(estimatedTime)}min
        </div>

        {driverLocation && (
          <div style={{
            position: 'absolute',
            bottom: '15px',
            left: '15px',
            right: '15px',
            background: 'rgba(255,87,34,0.95)',
            color: 'white',
            padding: '10px',
            borderRadius: '10px',
            fontSize: '12px',
            boxShadow: '0 2px 10px rgba(0,0,0,0.2)',
            zIndex: 1000
          }}>
            <div style={{ fontWeight: 'bold', marginBottom: '3px' }}>
              üìç Driver Live Location
            </div>
            <div style={{ opacity: 0.9 }}>
              {driverLocation.latitude.toFixed(4)}, {driverLocation.longitude.toFixed(4)}
            </div>
            {lastUpdate && (
              <div style={{ opacity: 0.8, fontSize: '10px', marginTop: '3px' }}>
                Updated: {lastUpdate.toLocaleTimeString()}
              </div>
            )}
          </div>
        )}

        {!driverLocation && (
          <div style={{
            position: 'absolute',
            bottom: '15px',
            left: '15px',
            right: '15px',
            background: 'rgba(0,0,0,0.7)',
            color: 'white',
            padding: '10px',
            borderRadius: '10px',
            fontSize: '12px',
            textAlign: 'center',
            zIndex: 1000
          }}>
            üìç Waiting for driver to share location...
          </div>
        )}
      </div>

      <div style={{
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        borderRadius: '12px',
        padding: '15px',
        color: 'white',
        marginTop: '15px'
      }}>
        <div style={{ display: 'flex', alignItems: 'center', marginBottom: '10px' }}>
          <div style={{
            width: '12px',
            height: '12px',
            background: '#4CAF50',
            borderRadius: '50%',
            marginRight: '8px',
            animation: 'pulse 2s infinite'
          }} />
          <span style={{ fontSize: '14px', fontWeight: 'bold' }}>Live Location</span>
        </div>

        {driverLocation ? (
          <>
            <div style={{ fontSize: '16px', marginBottom: '8px' }}>
              üìç {driverLocation.address || 'Live location'}
            </div>

            <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '11px', opacity: 0.8 }}>
              <span>{driverLocation.latitude.toFixed(4)}, {driverLocation.longitude.toFixed(4)}</span>
              <span>Updated: {lastUpdate?.toLocaleTimeString()}</span>
            </div>
          </>
        ) : (
          <div style={{ fontSize: '14px', opacity: 0.8 }}>
            Waiting for driver to share location...
          </div>
        )}
      </div>

      <style>
        {`
          @keyframes pulse {
            0%, 100% { opacity: 1; }
            50% { opacity: 0.5; }
          }
        `}
      </style>
    </div>
  );
};

export default LiveTrackingMap;
